import React, { useEffect, useState } from "react";
import api from "../../api";
import UserLayout from "../../layouts/UserLayout";

export default function DocumentManagementUser() {
  const [docs, setDocs] = useState([]);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [loading, setLoading] = useState(false);

  const [currentUserId, setCurrentUserId] = useState(null);
  const [currentRole, setCurrentRole] = useState(null);
  const [currentUserDept, setCurrentUserDept] = useState(null);

  // ==========================
  // L·∫•y userId & role t·ª´ token
  // ==========================
  useEffect(() => {
    const token =
      localStorage.getItem("token") ||
      JSON.parse(localStorage.getItem("authUser") || "{}")?.token;

    if (token) {
      try {
        const decoded = JSON.parse(atob(token.split(".")[1]));
        setCurrentUserId(decoded?.id);
        setCurrentRole(decoded?.role);
      } catch (err) {
        console.error("‚ùå L·ªói gi·∫£i m√£ token:", err);
      }
    }
  }, []);

  // =====================================
  // L·∫•y ph√≤ng ban hi·ªán t·∫°i c·ªßa nh√¢n vi√™n
  // =====================================
  useEffect(() => {
    const fetchDept = async () => {
      try {
        const res = await api.get("/employees/me");
        setCurrentUserDept(res.data.department);
      } catch (err) {
        console.error("‚ùå L·ªói l·∫•y ph√≤ng ban:", err);
      }
    };
    fetchDept();
  }, []);

  // =====================
  // Load t√†i li·ªáu
  // =====================
  const load = async () => {
    try {
      const res = await api.get("/employees/documents");
      setDocs(res.data || []);
    } catch (err) {
      alert(err.response?.data?.error || "‚ùå Kh√¥ng th·ªÉ t·∫£i t√†i li·ªáu!");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // =====================
  // Download file
  // =====================
  const downloadFile = async (id) => {
    try {
      const response = await api.get(`/employees/documents/download/${id}`, {
        responseType: "blob",
      });

      let filename = "tai-lieu";
      const disposition = response.headers["content-disposition"];
      if (disposition) {
        const utf8 = disposition.match(/filename\*=UTF-8''(.+)/);
        const normal = disposition.match(/filename="(.+)"/);
        filename = decodeURIComponent(utf8?.[1] || normal?.[1] || filename);
      }

      const blob = new Blob([response.data]);
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");

      link.href = url;
      link.download = filename;
      link.click();

      window.URL.revokeObjectURL(url);
    } catch (err) {
      alert("‚ùå L·ªói t·∫£i file");
    }
  };

  // =====================
  // Upload t√†i li·ªáu
  // =====================
  const uploadFile = async (e) => {
    e.preventDefault();
    if (!title || !file) return alert("Vui l√≤ng nh·∫≠p ti√™u ƒë·ªÅ & ch·ªçn file!");

    try {
      setLoading(true);
      const fd = new FormData();
      fd.append("title", title);
      fd.append("file", file);

      await api.post("/employees/documents", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("‚úÖ T·∫£i l√™n th√†nh c√¥ng!");
      setFile(null);
      setTitle("");
      load();
    } catch (err) {
      alert("‚ùå L·ªói t·∫£i t√†i li·ªáu");
    } finally {
      setLoading(false);
    }
  };

  // =====================
  // X√≥a t√†i li·ªáu
  // =====================
  const deleteFile = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a t√†i li·ªáu n√†y?")) return;

    try {
      await api.delete(`/employees/documents/${id}`);
      alert("üóëÔ∏è ƒê√£ x√≥a t√†i li·ªáu");
      load();
    } catch (err) {
      alert("‚ùå Kh√¥ng th·ªÉ x√≥a t√†i li·ªáu");
    }
  };

  // =====================
  // L·ªçc t√†i li·ªáu theo ph√≤ng ban
  // =====================
  const visibleDocs = docs.filter(
    (d) => d.department === "general" || d.department === currentUserDept
  );

  // =====================
  // Render UI (UserLayout)
  // =====================
  return (
    <UserLayout>
      <h3 className="mb-3">üìÇ T√†i li·ªáu ph√≤ng ban c·ªßa t√¥i</h3>

      {/* Upload t√†i li·ªáu */}
      <form className="card p-3 mb-3" onSubmit={uploadFile}>
        <h5 className="mb-3">‚¨ÜÔ∏è T·∫£i t√†i li·ªáu l√™n</h5>

        <input
          type="text"
          className="form-control mb-2"
          placeholder="Nh·∫≠p ti√™u ƒë·ªÅ..."
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />

        <input
          type="file"
          className="form-control mb-2"
          onChange={(e) => setFile(e.target.files[0])}
        />

        <button className="btn btn-primary" disabled={loading}>
          {loading ? "ƒêang t·∫£i l√™n..." : "üì§ T·∫£i l√™n"}
        </button>
      </form>

      {/* Danh s√°ch t√†i li·ªáu */}
      <div className="card p-3">
        <h5>üìë Danh s√°ch t√†i li·ªáu</h5>

        {visibleDocs.length === 0 ? (
          <p>Ch∆∞a c√≥ t√†i li·ªáu n√†o.</p>
        ) : (
          <table className="table table-bordered mt-2">
            <thead className="table-light">
              <tr>
                <th>Ph√≤ng ban</th>
                <th>Th∆∞ m·ª•c</th>
                <th>T√†i li·ªáu</th>
                <th>Ng∆∞·ªùi t·∫£i l√™n</th>
                <th className="text-center">H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {visibleDocs.map((d) => {
                const uploadedBy = d.uploadedBy?._id || d.uploadedBy;
                const isOwner = String(uploadedBy) === String(currentUserId);
                const isAdmin = currentRole === "admin";
                const sameDept =
                  d.department !== "general" &&
                  d.department === currentUserDept;

                return (
                  <tr key={d._id}>
                    <td>{d.department}</td>
                    <td>{d.folder || "‚Äî"}</td>
                    <td>{d.title}</td>
                    <td>{d.uploadedBy?.username || "Kh√¥ng r√µ"}</td>

                    <td className="text-center">
                      <button
                        className="btn btn-sm btn-success me-2"
                        onClick={() => downloadFile(d._id)}
                      >
                        ‚¨áÔ∏è T·∫£i
                      </button>

                      {(isOwner || isAdmin || sameDept) && (
                        <button
                          className="btn btn-sm btn-danger"
                          onClick={() => deleteFile(d._id)}
                        >
                          üóëÔ∏è X√≥a
                        </button>
                      )}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        )}
      </div>
    </UserLayout>
  );
}
