import React, { useEffect, useState } from "react";
import api from "../../api";
import AdminLayout from "../../layouts/AdminLayout";

function ReportAdmin() {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  const loadReports = async () => {
    setLoading(true);
    try {
      // L·∫•y d·ªØ li·ªáu b√°o c√°o
      const res = await api.get("/report");

      // L√†m s·∫°ch username n·∫øu b·ªã ObjectId
      const cleanData = res.data.map((item) => {
        const username =
          typeof item.username === "object" ? "" : item.username;

        return { ...item, username };
      });

      setReports(cleanData);
    } catch (e) {
      console.error("‚ùå L·ªói t·∫£i b√°o c√°o:", e);
      setErr(e.response?.data?.error || "Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu b√°o c√°o.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadReports();
  }, []);

  return (
    <AdminLayout>
      <h3>üìä B√°o c√°o t·ªïng h·ª£p</h3>

      {/* Loading */}
      {loading && <p>‚è≥ ƒêang t·∫£i d·ªØ li·ªáu...</p>}

      {/* L·ªói */}
      {err && <div className="alert alert-danger mt-2">{err}</div>}

      {/* B·∫£ng b√°o c√°o */}
      {!loading && !err && (
        <table className="table table-bordered table-striped mt-3">
          <thead>
            <tr>
              <th>Nh√¢n vi√™n</th>
              <th>Ph√≤ng ban</th>
              <th>Ch·ª©c v·ª•</th>
              <th>T·ªïng gi·ªù l√†m</th>
              <th>T·ªïng ng√†y ngh·ªâ</th>
              <th>Hi·ªáu su·∫•t TB</th>
            </tr>
          </thead>

          <tbody>
            {reports.map((r, idx) => (
              <tr key={idx}>
                <td>
                  {r.name} {r.username && `(${r.username})`}
                </td>
                <td>{r.department}</td>
                <td>{r.position}</td>
                <td>{r.totalHours?.toFixed?.(2) || 0}</td>
                <td>{r.totalLeaves || 0}</td>
                <td>{r.avgScore}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </AdminLayout>
  );
}

export default ReportAdmin;
