import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function LeaveApproval() {
  const [list, setList] = useState([]);

  const load = async () => {
    const res = await api.get("/admin/leave-requests");
    setList(res.data);
  };
  useEffect(() => { load(); }, []);

  const act = async (id, type) => {
    await api.put(`/admin/leave-requests/${id}/${type}`);
    load();
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üìå Ph√™ duy·ªát ngh·ªâ ph√©p</h3>
          <table className="table table-bordered">
            <thead><tr><th>Nh√¢n vi√™n</th><th>T·ª´</th><th>ƒê·∫øn</th><th>L√Ω do</th><th>Tr·∫°ng th√°i</th><th>H√†nh ƒë·ªông</th></tr></thead>
            <tbody>
              {list.map(l => (
                <tr key={l._id}>
                  <td>{l.userId?.username}</td>
                  <td>{new Date(l.from).toLocaleDateString()}</td>
                  <td>{new Date(l.to).toLocaleDateString()}</td>
                  <td>{l.reason}</td>
                  <td>{l.status}</td>
                  <td>
                    {l.status === "pending" && (
                      <>
                        <button className="btn btn-success btn-sm me-2" onClick={()=>act(l._id, "approve")}>Duy·ªát</button>
                        <button className="btn btn-danger btn-sm" onClick={()=>act(l._id, "reject")}>T·ª´ ch·ªëi</button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default LeaveApproval;
