import React, { useEffect, useState } from "react";
import api from "../../api";
import AdminLayout from "../../layouts/AdminLayout";

export default function LeaveApproval() {
  const [list, setList] = useState([]);

  const load = async () => {
    try {
      const res = await api.get("/admin/leave-requests");
      setList(res.data || []);
    } catch (err) {
      console.error("‚ùå L·ªói load ngh·ªâ ph√©p:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c danh s√°ch ngh·ªâ ph√©p");
    }
  };

  useEffect(() => {
    load();
  }, []);

  const act = async (id, type) => {
    try {
      await api.put(`/admin/leave-requests/${id}/${type}`);
      load();
    } catch (err) {
      alert("‚ùå L·ªói c·∫≠p nh·∫≠t tr·∫°ng th√°i");
    }
  };

  const fmtDate = (d) =>
    d ? new Date(d).toLocaleDateString("vi-VN") : "‚Äî";

  const calcDays = (from, to) => {
    if (!from || !to) return 0;
    const a = new Date(from);
    const b = new Date(to);
    return Math.round((b - a) / (1000 * 60 * 60 * 24)) + 1;
  };

  return (
    <AdminLayout>
      <h2 className="mb-4">üìå Ph√™ duy·ªát ngh·ªâ ph√©p</h2>

      <div className="card p-3">
        <h5 className="mb-3">Danh s√°ch y√™u c·∫ßu ngh·ªâ ph√©p</h5>

        <div className="table-responsive">
          <table className="table table-bordered table-hover align-middle">
            <thead className="table-light">
              <tr>
                <th>Username</th>
                <th>T√™n th·∫≠t</th>
                <th>Ph√≤ng ban</th>
                <th>Ch·ª©c v·ª•</th>
                <th>T·ª´ ng√†y</th>
                <th>ƒê·∫øn ng√†y</th>
                <th>S·ªë ng√†y</th>
                <th>L√Ω do</th>
                <th>Ng√†y t·∫°o</th>
                <th>Tr·∫°ng th√°i</th>
                <th className="text-center" style={{ width: 180 }}>H√†nh ƒë·ªông</th>
              </tr>
            </thead>

            <tbody>
              {list.map((l) => (
                <tr key={l._id}>
                  <td>{l.userId?.username || "‚Äî"}</td>
                  <td>{l.userId?.name || "‚Äî"}</td>
                  <td>{l.userId?.department || "‚Äî"}</td>
                  <td>{l.userId?.position || "‚Äî"}</td>

                  <td>{fmtDate(l.startDate)}</td>
                  <td>{fmtDate(l.endDate)}</td>
                  <td className="fw-bold text-primary">
                    {calcDays(l.startDate, l.endDate)} ng√†y
                  </td>

                  <td style={{ maxWidth: 200, whiteSpace: "pre-wrap" }}>
                    {l.reason}
                  </td>

                  <td>
                    {l.createdAt
                      ? new Date(l.createdAt).toLocaleString("vi-VN")
                      : "‚Äî"}
                  </td>

                  <td>
                    {l.status === "pending" && (
                      <span className="badge bg-warning text-dark">‚è≥ Ch·ªù duy·ªát</span>
                    )}
                    {l.status === "approved" && (
                      <span className="badge bg-success">‚úÖ ƒê√£ duy·ªát</span>
                    )}
                    {l.status === "rejected" && (
                      <span className="badge bg-danger">‚ùå T·ª´ ch·ªëi</span>
                    )}
                  </td>

                  <td className="text-center">
                    {l.status === "pending" ? (
                      <>
                        <button
                          className="btn btn-success btn-sm me-2"
                          onClick={() => act(l._id, "approve")}
                        >
                          Duy·ªát
                        </button>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => act(l._id, "reject")}
                        >
                          T·ª´ ch·ªëi
                        </button>
                      </>
                    ) : (
                      <span className="text-muted">‚Äî</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </AdminLayout>
  );
}
