import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function Attendance() {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);

  const load = async () => {
    try {
      const res = await api.get("/employees/attendance");
      setRecords(res.data);
    } catch (err) {
      console.error("❌ Lỗi load chấm công:", err);
      alert("Lỗi tải dữ liệu chấm công!");
    } finally {
      setLoading(false);
    }
  };

  const checkIn = async () => {
    try {
      await api.post("/employees/attendance/check-in");
      alert("✅ Check-in thành công");
      load();
    } catch (err) {
      alert(err.response?.data?.error || "❌ Lỗi check-in");
    }
  };

  const checkOut = async () => {
    try {
      await api.post("/employees/attendance/check-out");
      alert("✅ Check-out thành công");
      load();
    } catch (err) {
      alert(err.response?.data?.error || "❌ Lỗi check-out");
    }
  };

  useEffect(() => {
    load();
  }, []);

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="employee" />
        </div>

        <div className="col-9">
          <h3>🕒 Chấm công</h3>

          <div className="mb-3">
            <button className="btn btn-success me-2" onClick={checkIn}>✅ Check-in</button>
            <button className="btn btn-danger" onClick={checkOut}>⏹ Check-out</button>
          </div>

          {loading ? (
            <p>⏳ Đang tải dữ liệu...</p>
          ) : (
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Ngày</th>
                  <th>Giờ vào</th>
                  <th>Giờ ra</th>
                  <th>Tổng giờ</th>
                </tr>
              </thead>
              <tbody>
                {records.map((r) => (
                  <tr key={r._id}>
                    <td>{r.date}</td>
                    <td>{r.checkIn ? new Date(r.checkIn).toLocaleTimeString() : "—"}</td>
                    <td>{r.checkOut ? new Date(r.checkOut).toLocaleTimeString() : "—"}</td>
                    <td>{r.totalHours?.toFixed(2) || 0}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
}

export default Attendance;
