import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function PerformanceReviewAdmin() {
  const [reviews, setReviews] = useState([]);
  const [form, setForm] = useState({ userId: "", score: 0, feedback: "" });
  const [employees, setEmployees] = useState([]);

  const load = async () => {
    const res = await api.get("/admin/performance");
    setReviews(res.data);
    const emp = await api.get("/admin/employees");
    setEmployees(emp.data);
  };

  useEffect(() => { load(); }, []);

  const addReview = async () => {
    try {
      await api.post("/admin/performance", form);
      alert("✅ Đã thêm đánh giá");
      setForm({ userId: "", score: 0, feedback: "" });
      load();
    } catch (err) {
      alert("❌ Lỗi thêm đánh giá");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>📊 Đánh giá hiệu suất</h3>
          <div className="card p-3 mb-3">
            <select className="form-control mb-2"
              value={form.userId}
              onChange={(e) => setForm({ ...form, userId: e.target.value })}>
              <option value="">-- Chọn nhân viên --</option>
              {employees.map(emp => (
                <option key={emp._id} value={emp.userId?._id}>{emp.name}</option>
              ))}
            </select>
            <input type="number" className="form-control mb-2" placeholder="Điểm"
              value={form.score} onChange={(e) => setForm({ ...form, score: e.target.value })} />
            <textarea className="form-control mb-2" placeholder="Nhận xét"
              value={form.feedback} onChange={(e) => setForm({ ...form, feedback: e.target.value })} />
            <button className="btn btn-primary" onClick={addReview}>Thêm</button>
          </div>

          <table className="table table-bordered">
            <thead><tr><th>Nhân viên</th><th>Điểm</th><th>Nhận xét</th></tr></thead>
            <tbody>
              {reviews.map(r => (
                <tr key={r._id}>
                  <td>{r.userId?.username}</td>
                  <td>{r.score}</td>
                  <td>{r.feedback}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
export default PerformanceReviewAdmin;
