import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

const fmtMoney = (v) =>
  typeof v === "number" ? v.toLocaleString("vi-VN") : (Number(v || 0)).toLocaleString("vi-VN");
const fmtPercent = (v) =>
  v ? `${(Number(v) * 100).toFixed(0)}%` : "‚Äì";
const ymNow = () => {
  const d = new Date();
  return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, "0")}`;
};

export default function SalaryManagement() {
  const [month, setMonth] = useState(ymNow()); // hi·ªán ch∆∞a d√πng query ·ªü /salary/me, nh∆∞ng ƒë·ªÉ s·∫µn UI
  const [list, setList] = useState([]);
  const [loading, setLoading] = useState(false);

  const load = async () => {
    try {
      setLoading(true);
      // BE tr·∫£ v·ªÅ m·∫£ng 1 ph·∫ßn t·ª≠ (controller m·ªõi)
      const res = await api.get("/salary/me");
      const rows = (res.data || []).map((s) => ({
        ...s,
        month: s.month || month,
        totalDays: s.totalDays ?? 0,
        totalLateMinutes: s.totalLateMinutes ?? 0,
        overtimeHours: s.overtimeHours ?? 0,
        overtimePay: s.overtimePay ?? (Number(s.overtimeHours || 0) * Number(s.overtimeRate || 0)),
        penalty: s.penalty ?? 0,
        penaltyRate: s.penaltyRate ?? 0,
        dailyRate: s.dailyRate ?? 0,
        overtimeRate: s.overtimeRate ?? 0,
        amount: s.amount ?? 0,
      }));
      setList(rows);
    } catch (err) {
      console.error(err);
      alert("‚ùå L·ªói t·∫£i d·ªØ li·ªáu l∆∞∆°ng");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const authUser = localStorage.getItem("authUser");
    if (authUser) load();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [month]);

  const row = list[0];

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="user" />
        </div>

        <div className="col-9">
          <div className="d-flex align-items-center justify-content-between mb-2">
            <h3 className="mb-0">üí∞ L∆∞∆°ng c·ªßa t√¥i</h3>
            <div className="d-flex gap-2 align-items-center">
              <input
                type="month"
                className="form-control"
                style={{ width: 180 }}
                value={month}
                onChange={(e) => setMonth(e.target.value)}
              />
              <button className="btn btn-outline-secondary" onClick={load} disabled={loading}>
                ‚ü≥ {loading ? "ƒêang t·∫£i..." : "L√†m m·ªõi"}
              </button>
            </div>
          </div>

          {!row ? (
            <div className="alert alert-info">Kh√¥ng c√≥ d·ªØ li·ªáu l∆∞∆°ng.</div>
          ) : (
            <>
              {/* Cards */}
              <div className="row g-2 mb-3">
                <div className="col">
                  <div className="card p-2">
                    <div className="small text-muted">Th√°ng</div>
                    <div className="fw-bold">{row.month}</div>
                  </div>
                </div>
                <div className="col">
                  <div className="card p-2">
                    <div className="small text-muted">Ng√†y c√¥ng</div>
                    <div className="fw-bold">{row.totalDays}</div>
                  </div>
                </div>
                <div className="col">
                  <div className="card p-2">
                    <div className="small text-muted">Ph√∫t tr·ªÖ</div>
                    <div className="fw-bold">{row.totalLateMinutes}</div>
                  </div>
                </div>
                <div className="col">
                  <div className="card p-2">
                    <div className="small text-muted">Ph·∫°t</div>
                    <div className="fw-bold">{fmtMoney(row.penalty)} ƒë</div>
                  </div>
                </div>
                <div className="col">
                  <div className="card p-2">
                    <div className="small text-muted">% Ph·∫°t</div>
                    <div className="fw-bold">{fmtPercent(row.penaltyRate)}</div>
                  </div>
                </div>
                <div className="col">
                  <div className="card p-2">
                    <div className="small text-muted">TƒÉng ca (gi·ªù)</div>
                    <div className="fw-bold">{row.overtimeHours}</div>
                  </div>
                </div>
              </div>

              <div className="table-responsive">
                <table className="table table-striped align-middle">
                  <thead className="table-light">
                    <tr>
                      <th>Ti·ªÅn ng√†y (VND)</th>
                      <th>Ti·ªÅn tƒÉng ca/gi·ªù (VND)</th>
                      <th>Ti·ªÅn tƒÉng ca (VND)</th>
                      <th>Ph·∫°t (VND)</th>
                      <th>T·ªïng l∆∞∆°ng (VND)</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{fmtMoney(row.dailyRate)}</td>
                      <td>{fmtMoney(row.overtimeRate)}</td>
                      <td>{fmtMoney(row.overtimePay)}</td>
                      <td>{fmtMoney(row.penalty)}</td>
                      <td className="fw-bold">{fmtMoney(row.amount)}</td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
