// frontend/src/pages/user/DocumentManagementUser.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

export default function DocumentManagementUser() {
  const [docs, setDocs] = useState([]);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [loading, setLoading] = useState(false);
  const [currentUserId, setCurrentUserId] = useState(null);

  // ‚úÖ L·∫•y userId t·ª´ token khi mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const decoded = JSON.parse(atob(token.split(".")[1]));
        setCurrentUserId(decoded?.id);
      } catch (err) {
        console.error("‚ùå L·ªói gi·∫£i m√£ token:", err);
      }
    }
  }, []);

  // üì¶ L·∫•y danh s√°ch t√†i li·ªáu
  const load = async () => {
    try {
      const res = await api.get("/employees/documents"); // backend l·ªçc theo ph√≤ng ban
      setDocs(res.data || []);
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i danh s√°ch t√†i li·ªáu:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ t·∫£i danh s√°ch t√†i li·ªáu!");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // ‚¨áÔ∏è T·∫£i t√†i li·ªáu (g·ª≠i token k√®m)
  const downloadFile = async (id, title) => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch(
        `${process.env.REACT_APP_API_BASE || "http://localhost:5000/api"}/employees/documents/download/${id}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      if (!res.ok) throw new Error("Kh√¥ng th·ªÉ t·∫£i file");
      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = title || "Tai_lieu";
      a.click();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i file:", err);
      alert("Kh√¥ng th·ªÉ t·∫£i t√†i li·ªáu (c√≥ th·ªÉ do quy·ªÅn h·∫°n ho·∫∑c l·ªói token).");
    }
  };

  // ‚¨ÜÔ∏è Upload t√†i li·ªáu
  const uploadFile = async (e) => {
    e.preventDefault();
    if (!file || !title.trim()) return alert("Vui l√≤ng nh·∫≠p ti√™u ƒë·ªÅ v√† ch·ªçn file!");

    const formData = new FormData();
    formData.append("title", title);
    formData.append("file", file);

    try {
      setLoading(true);
      await api.post("/admin/documents", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("‚úÖ T·∫£i t√†i li·ªáu l√™n th√†nh c√¥ng!");
      setFile(null);
      setTitle("");
      await load();
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i l√™n:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ t·∫£i l√™n t√†i li·ªáu!");
    } finally {
      setLoading(false);
    }
  };

  // üóëÔ∏è X√≥a t√†i li·ªáu (ch·ªâ t√†i li·ªáu c·ªßa ch√≠nh m√¨nh)
  const deleteFile = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a t√†i li·ªáu n√†y kh√¥ng?")) return;
    try {
      await api.delete(`/admin/documents/${id}`);
      alert("üóëÔ∏è ƒê√£ x√≥a t√†i li·ªáu");
      await load();
    } catch (err) {
      console.error("‚ùå L·ªói x√≥a t√†i li·ªáu:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ x√≥a t√†i li·ªáu!");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        {/* Sidebar */}
        <div className="col-3">
          <SidebarMenu role="user" />
        </div>

        {/* N·ªôi dung ch√≠nh */}
        <div className="col-9">
          <h3>üìÇ T√†i li·ªáu ph√≤ng ban c·ªßa t√¥i</h3>

          {/* Upload form */}
          <form className="card p-3 mb-3" onSubmit={uploadFile}>
            <h5 className="mb-3">‚¨ÜÔ∏è T·∫£i t√†i li·ªáu l√™n</h5>
            <input
              type="text"
              className="form-control mb-2"
              placeholder="Nh·∫≠p ti√™u ƒë·ªÅ t√†i li·ªáu..."
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
            <input
              type="file"
              className="form-control mb-2"
              onChange={(e) => setFile(e.target.files[0])}
              required
            />
            <button className="btn btn-primary" disabled={loading}>
              {loading ? "ƒêang t·∫£i l√™n..." : "üì§ T·∫£i l√™n"}
            </button>
          </form>

          {/* B·∫£ng danh s√°ch */}
          <table className="table table-bordered align-middle">
            <thead className="table-light">
              <tr>
                <th>Ti√™u ƒë·ªÅ</th>
                <th>Ph√≤ng ban</th>
                <th>Ng∆∞·ªùi t·∫£i l√™n</th>
                <th>H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {docs.length > 0 ? (
                docs.map((d) => (
                  <tr key={d._id}>
                    <td>{d.title}</td>
                    <td>{d.department}</td>
                    <td>{d.uploadedBy?.username || "Kh√¥ng r√µ"}</td>
                    <td>
                      <button
                        className="btn btn-sm btn-success me-2"
                        onClick={() => downloadFile(d._id, d.title)}
                      >
                        ‚¨áÔ∏è T·∫£i
                      </button>

                      {/* Ch·ªâ hi·ªán n√∫t x√≥a n·∫øu l√† ng∆∞·ªùi ƒë√£ t·∫£i l√™n */}
                      {d.uploadedBy?._id === currentUserId && (
                        <button
                          className="btn btn-sm btn-danger"
                          onClick={() => deleteFile(d._id)}
                        >
                          üóëÔ∏è X√≥a
                        </button>
                      )}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={4} className="text-center text-muted">
                    Ch∆∞a c√≥ t√†i li·ªáu
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
