// frontend/src/pages/user/PerformanceReviewUser.js
import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function PerformanceReviewUser() {
  const [list, setList] = useState([]);

  useEffect(() => {
    const load = async () => {
      try {
        const res = await api.get("/admin/performance"); // user ch·ªâ xem
        setList(res.data);
      } catch (err) {
        console.error(err);
        alert("‚ùå L·ªói t·∫£i ƒë√°nh gi√°");
      }
    };
    load();
  }, []);

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="employee" /></div>
        <div className="col-9">
          <h3>üìë ƒê√°nh gi√° hi·ªáu su·∫•t c·ªßa t√¥i</h3>
          <ul className="list-group">
            {list.map((r) => (
              <li key={r._id} className="list-group-item">
                <strong>{r.userId?.username}</strong>: {r.review}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default PerformanceReviewUser;
