import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api";

function Register() {
  const [form, setForm] = useState({
    username: "",
    password: "",
    name: "",
    department: "",
    position: "",
    role: "employee",
  });
  const navigate = useNavigate();
  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post("/auth/register", form);
      alert("Đăng ký thành công, mời đăng nhập");
      navigate("/");
    } catch (err) {
      alert(err.response?.data?.error || "Có lỗi xảy ra");
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: 520 }}>
      <h3>Đăng ký tài khoản</h3>
      <form onSubmit={onSubmit}>
        <input name="username" className="form-control mb-2" placeholder="Username" onChange={onChange} />
        <input name="password" type="password" className="form-control mb-2" placeholder="Password" onChange={onChange} />
        <input name="name" className="form-control mb-2" placeholder="Họ tên" onChange={onChange} />
        <input name="department" className="form-control mb-2" placeholder="Phòng ban" onChange={onChange} />
        <input name="position" className="form-control mb-3" placeholder="Chức vụ" onChange={onChange} />
        <select name="role" className="form-control mb-3" value={form.role} onChange={onChange}>
          <option value="employee">Nhân viên</option>
          <option value="manager">Quản lý</option>
          <option value="admin">Admin</option>
        </select>
        <button className="btn btn-success w-100">Đăng ký</button>
      </form>
    </div>
  );
}

export default Register;
