import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";
import { useAuth } from "../../context/AuthContext";

const socket = io(
  process.env.REACT_APP_API_BASE?.replace("/api", "") || "http://localhost:5000",
  { transports: ["websocket"], autoConnect: true }
);

export default function ChatAdmin() {
  const { user } = useAuth();
  const me = user?.user;
  const [employees, setEmployees] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const bottomRef = useRef();

  useEffect(() => {
    if (me?._id) {
      socket.emit("join", { userId: me._id });
      loadRooms();
      loadEmployees();
    }
  }, [me]);

  const loadRooms = async () => {
    const res = await api.get("/messages/rooms");
    const combined = [];
    if (res.data.deptRoom) combined.push(res.data.deptRoom);
    if (res.data.privateRooms) combined.push(...res.data.privateRooms);
    setRooms(combined);
  };

  const loadEmployees = async () => {
    const res = await api.get("/admin/employees");
    setEmployees(res.data || []);
  };

  const loadMessages = async (room) => {
    const res = await api.get(`/messages/${room._id}`);
    setMessages(res.data || []);
    scrollToBottom();
  };

  const openRoom = async (room) => {
    setCurrentRoom(room);
    await loadMessages(room);
  };

  const startPrivateWith = async (userId) => {
    const res = await api.post("/messages/rooms/private", { otherUserId: userId });
    await loadRooms();
    setCurrentRoom(res.data);
    await loadMessages(res.data);
  };

  const scrollToBottom = () => {
    setTimeout(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), 50);
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!text.trim() || !currentRoom) return;
    const content = text.trim();

    const saved = await api.post("/messages", { roomId: currentRoom._id, content });

    socket.emit("send_message", {
      roomId: currentRoom._id,
      content,
      fromUserId: me?._id,
      fromUserName: me?.username,
      createdAt: new Date().toISOString(),
    });

    const myMsg = { ...saved.data, sender: { _id: me?._id, username: me?.username } };
    setMessages((prev) => [...prev, myMsg]);
    setText("");
    scrollToBottom();
  };

  useEffect(() => {
    const handler = (payload) => {
      if (!currentRoom || payload.roomId !== currentRoom._id) return;
      if (payload?.sender?._id === me?._id) return;
      setMessages((prev) => [...prev, payload]);
      scrollToBottom();
    };
    socket.on("receive_message", handler);
    return () => socket.off("receive_message", handler);
  }, [currentRoom]);

  const isMine = (m) => {
    const sid = String(m?.sender?._id || m?.fromUserId || "");
    return sid === String(me?._id);
  };

  const renderBubble = (m) => {
    const mine = isMine(m);
    return (
      <div
        key={m._id}
        className={`d-flex mb-2 ${mine ? "justify-content-end" : "justify-content-start"}`}
      >
        <div
          className={`p-2 px-3 rounded-4 shadow-sm ${
            mine ? "bg-primary text-white align-self-end" : "bg-light text-dark align-self-start"
          }`}
          style={{
            maxWidth: "70%",
            borderRadius: mine ? "18px 18px 2px 18px" : "18px 18px 18px 2px",
          }}
        >
          {!mine && (
            <div className="small text-muted mb-1">{m.sender?.username}</div>
          )}
          <div>{m.content}</div>
          <div
            className={`small text-muted mt-1 ${mine ? "text-end" : ""}`}
            style={{ fontSize: "0.75rem" }}
          >
            {new Date(m.createdAt).toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="container-fluid mt-3">
      <div className="row">
        {/* Sidebar */}
        <div className="col-3">
          <SidebarMenu role="admin" />
          <div className="card mt-3">
            <div className="card-header"><strong>üí¨ Chat (Admin)</strong></div>
            <div className="card-body p-2">
              <small className="text-muted">Danh s√°ch nh√¢n vi√™n</small>
              <div className="list-group mt-1" style={{ maxHeight: 300, overflowY: "auto" }}>
                {employees.map((e) => (
                  <button
                    key={e._id}
                    className="list-group-item list-group-item-action"
                    onClick={() => startPrivateWith(e.userId?._id)}
                  >
                    {e.name} <span className="text-muted">({e.userId?.username})</span>
                  </button>
                ))}
              </div>

              <hr className="my-2" />
              <small className="text-muted">C√°c ph√≤ng chat</small>
              <div className="list-group mt-1" style={{ maxHeight: 300, overflowY: "auto" }}>
                {rooms.map((r) => (
                  <button
                    key={r._id}
                    className="list-group-item list-group-item-action"
                    onClick={() => openRoom(r)}
                  >
                    {r.name || r.participants?.map((p) => p.username).join(", ")}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Khung chat */}
        <div className="col-9">
          <div className="card">
            <div className="card-header">
              {currentRoom
                ? currentRoom.name ||
                  `üí¨ ${(currentRoom.participants || [])
                    .map((p) => p.username)
                    .join(", ")}`
                : "Ch·ªçn ƒëo·∫°n chat ƒë·ªÉ b·∫Øt ƒë·∫ßu"}
            </div>
            <div className="card-body" style={{ height: 520, overflowY: "auto" }}>
              {messages.map(renderBubble)}
              <div ref={bottomRef} />
            </div>
            <div className="card-footer">
              <form onSubmit={sendMessage} className="d-flex gap-2">
                <input
                  className="form-control"
                  placeholder="Nh·∫≠p tin nh·∫Øn..."
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                />
                <button
                  className="btn btn-primary"
                  disabled={!currentRoom || !text.trim()}
                >
                  G·ª≠i
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
