import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function Attendance() {
  const [records, setRecords] = useState([]);

  const load = () => api.get("/attendance/me").then(res => setRecords(res.data));

  useEffect(() => { load(); }, []);

  const checkin = async () => { await api.post("/attendance/checkin"); load(); };
  const checkout = async () => { await api.post("/attendance/checkout"); load(); };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="user" /></div>
        <div className="col-9">
          <h3>⏱ Chấm công</h3>
          <div className="mb-3">
            <button className="btn btn-success me-2" onClick={checkin}>Check-in</button>
            <button className="btn btn-danger" onClick={checkout}>Check-out</button>
          </div>
          <table className="table table-bordered">
            <thead><tr><th>Thời gian</th><th>Loại</th></tr></thead>
            <tbody>
              {records.map((r, idx) => (
                <tr key={idx}>
                  <td>{new Date(r.time).toLocaleString()}</td>
                  <td>{r.type}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
export default Attendance;
