// frontend/src/pages/admin/DocumentManagementAdmin.js
import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function DocumentManagementAdmin() {
  const [docs, setDocs] = useState([]);
  const [form, setForm] = useState({ title: "", url: "" });

  const load = async () => {
    try {
      const res = await api.get("/admin/documents");
      setDocs(res.data);
    } catch (err) {
      console.error(err);
      alert("‚ùå L·ªói t·∫£i t√†i li·ªáu");
    }
  };

  useEffect(() => { load(); }, []);

  const add = async () => {
    try {
      await api.post("/admin/documents", form);
      setForm({ title: "", url: "" });
      load();
      alert("‚úÖ Th√™m t√†i li·ªáu th√†nh c√¥ng");
    } catch (err) {
      console.error(err);
      alert("‚ùå L·ªói th√™m t√†i li·ªáu");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üìÇ Qu·∫£n l√Ω t√†i li·ªáu (Admin)</h3>
          <input
            className="form-control mb-2"
            placeholder="T√™n t√†i li·ªáu"
            value={form.title}
            onChange={(e) => setForm({ ...form, title: e.target.value })}
          />
          <input
            className="form-control mb-2"
            placeholder="ƒê∆∞·ªùng d·∫´n (URL)"
            value={form.url}
            onChange={(e) => setForm({ ...form, url: e.target.value })}
          />
          <button className="btn btn-primary" onClick={add}>Th√™m</button>

          <ul className="list-group mt-3">
            {docs.map((d) => (
              <li key={d._id} className="list-group-item">
                <a href={d.url} target="_blank" rel="noreferrer">{d.title}</a>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default DocumentManagementAdmin;
