import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function WorkScheduleAdmin() {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({
    title: "", description: "", department: "",
    assignedTo: "", startDate: "", endDate: ""
  });

  const load = async () => {
    const res = await api.get("/admin/work-schedule");
    setList(res.data);
  };
  useEffect(() => { load(); }, []);

  const add = async () => {
    await api.post("/admin/work-schedule", form);
    setForm({ title: "", description: "", department: "", assignedTo: "", startDate: "", endDate: "" });
    load();
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>📅 Quản lý lịch làm việc</h3>

          {/* Form thêm công việc */}
          <div className="card p-3 mb-3">
            <input className="form-control mb-2" placeholder="Tên công việc"
              value={form.title} onChange={e => setForm({...form, title: e.target.value})}/>
            <textarea className="form-control mb-2" placeholder="Mô tả"
              value={form.description} onChange={e => setForm({...form, description: e.target.value})}/>
            <input className="form-control mb-2" placeholder="Phòng ban"
              value={form.department} onChange={e => setForm({...form, department: e.target.value})}/>
            <input className="form-control mb-2" placeholder="UserId (tùy chọn)"
              value={form.assignedTo} onChange={e => setForm({...form, assignedTo: e.target.value})}/>
            <input type="date" className="form-control mb-2"
              value={form.startDate} onChange={e => setForm({...form, startDate: e.target.value})}/>
            <input type="date" className="form-control mb-2"
              value={form.endDate} onChange={e => setForm({...form, endDate: e.target.value})}/>
            <button className="btn btn-primary" onClick={add}>➕ Thêm công việc</button>
          </div>

          {/* Bảng danh sách */}
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Công việc</th><th>Phòng ban</th><th>Giao cho</th>
                <th>Bắt đầu</th><th>Kết thúc</th><th>Trạng thái</th>
              </tr>
            </thead>
            <tbody>
              {list.map(w => (
                <tr key={w._id}>
                  <td>{w.title}</td>
                  <td>{w.department}</td>
                  <td>{w.assignedTo?.username || "Cả phòng"}</td>
                  <td>{new Date(w.startDate).toLocaleDateString()}</td>
                  <td>{new Date(w.endDate).toLocaleDateString()}</td>
                  <td>{w.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default WorkScheduleAdmin;
