import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function PerformanceReviewAdmin() {
  const [reviews, setReviews] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [editId, setEditId] = useState(null);

  const [form, setForm] = useState({
    userId: "",
    period: "",
    completedTasks: 0,
    communication: 0,
    technical: 0,
    attitude: 0,
    quality: "",
  });

  const load = async () => {
    try {
      const res = await api.get("/admin/performance");
      setReviews(res.data);
      const empRes = await api.get("/admin/employees");
      setEmployees(empRes.data);
    } catch (err) {
      console.error("❌ API Error:", err);
      alert("❌ Không tải được dữ liệu");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // Tính điểm chất lượng công việc
  const calcQuality = () => {
    const totalScore =
      Number(form.communication) +
      Number(form.technical) +
      Number(form.attitude) +
      taskScore(form.completedTasks);

    const avg = totalScore / 4;
    if (avg >= 8) return "Tốt";
    if (avg >= 5) return "Trung bình";
    return "Kém";
  };

  // Quy đổi số công việc sang điểm
  const taskScore = (tasks) => {
    if (tasks >= 30) return 10;
    if (tasks >= 20) return 7;
    if (tasks >= 10) return 5;
    return 2;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = { ...form, quality: calcQuality() };
      if (editId) {
        await api.put(`/admin/performance/${editId}`, data);
        alert("✅ Đã cập nhật đánh giá");
      } else {
        await api.post("/admin/performance", data);
        alert("✅ Đã thêm đánh giá");
      }
      setForm({
        userId: "",
        period: "",
        completedTasks: 0,
        communication: 0,
        technical: 0,
        attitude: 0,
        quality: "",
      });
      setEditId(null);
      load();
    } catch (err) {
      console.error("❌ Lỗi thêm/cập nhật:", err);
      alert("❌ Không thể lưu đánh giá");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Bạn có chắc muốn xóa đánh giá này?")) return;
    try {
      await api.delete(`/admin/performance/${id}`);
      alert("🗑️ Đã xóa đánh giá");
      load();
    } catch (err) {
      console.error("❌ Lỗi xóa:", err);
    }
  };

  const startEdit = (r) => {
    setEditId(r._id);
    setForm({
      userId: r.userId?._id || "",
      period: r.period,
      completedTasks: r.completedTasks || 0,
      communication: r.communication || 0,
      technical: r.technical || 0,
      attitude: r.attitude || 0,
      quality: r.quality || "",
    });
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>📊 Đánh giá hiệu suất nhân viên</h3>

          {/* Form */}
          <form className="card p-3 mb-3" onSubmit={handleSubmit}>
            <select className="form-control mb-2"
              value={form.userId}
              onChange={(e) => setForm({ ...form, userId: e.target.value })}>
              <option value="">-- Chọn nhân viên --</option>
              {employees.map(emp => (
                <option key={emp._id} value={emp.userId?._id}>{emp.name}</option>
              ))}
            </select>

            <input type="text" className="form-control mb-2"
              placeholder="Kỳ đánh giá (VD: Tháng 1/2025)"
              value={form.period}
              onChange={(e) => setForm({ ...form, period: e.target.value })} />

            <input type="number" className="form-control mb-2"
              placeholder="Số công việc hoàn thành"
              value={form.completedTasks}
              onChange={(e) => setForm({ ...form, completedTasks: e.target.value })} />

            <label>Kỹ năng giao tiếp</label>
            <select className="form-control mb-2"
              value={form.communication}
              onChange={(e) => setForm({ ...form, communication: e.target.value })}>
              <option value={0}>Không biết (0)</option>
              <option value={2}>Kém (2)</option>
              <option value={4}>Có thể giao tiếp (4)</option>
              <option value={6}>Trung bình (6)</option>
              <option value={8}>Khá (8)</option>
              <option value={10}>Giỏi (10)</option>
            </select>

            <label>Kỹ năng kỹ thuật</label>
            <select className="form-control mb-2"
              value={form.technical}
              onChange={(e) => setForm({ ...form, technical: e.target.value })}>
              <option value={0}>Không biết (0)</option>
              <option value={2}>Kém (2)</option>
              <option value={4}>Có thể (4)</option>
              <option value={6}>Trung bình (6)</option>
              <option value={8}>Khá (8)</option>
              <option value={10}>Giỏi (10)</option>
            </select>

            <label>Thái độ</label>
            <select className="form-control mb-2"
              value={form.attitude}
              onChange={(e) => setForm({ ...form, attitude: e.target.value })}>
              <option value={10}>Tích cực (10)</option>
              <option value={0}>Tiêu cực (0)</option>
            </select>

            <div className="mb-2">
              <label>Chất lượng công việc (tự động)</label>
              <input type="text" className="form-control"
                value={calcQuality()} readOnly />
            </div>

            <button className="btn btn-primary">
              {editId ? "💾 Cập nhật" : "➕ Thêm"}
            </button>
          </form>

          {/* Bảng */}
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Nhân viên</th>
                <th>Kỳ</th>
                <th>Công việc</th>
                <th>Giao tiếp</th>
                <th>Kỹ thuật</th>
                <th>Thái độ</th>
                <th>Chất lượng</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {reviews.map(r => (
                <tr key={r._id}>
                  <td>{r.userId?.username}</td>
                  <td>{r.period}</td>
                  <td>{r.completedTasks}</td>
                  <td>{r.communication}</td>
                  <td>{r.technical}</td>
                  <td>{r.attitude}</td>
                  <td>{r.quality}</td>
                  <td>
                    <button className="btn btn-warning btn-sm me-2" onClick={() => startEdit(r)}>Sửa</button>
                    <button className="btn btn-danger btn-sm" onClick={() => handleDelete(r._id)}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

        </div>
      </div>
    </div>
  );
}

export default PerformanceReviewAdmin;
