import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api";
import { useAuth } from "../context/AuthContext";

import "../styles/auth.css";
import "../styles/login.css";

function Login() {
  const navigate = useNavigate();
  const { login } = useAuth();

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await api.post("/auth/login", { username, password });

      login({
        token: res.data.token,
        user: res.data.user
      });

      const role = res.data.user.role;
      if (role === "admin") navigate("/admin/dashboard");
      else navigate("/user/dashboard");

    } catch (err) {
      alert(err.response?.data?.error || "Sai t√™n ƒëƒÉng nh·∫≠p ho·∫∑c m·∫≠t kh·∫©u!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2 className="auth-title">üîê ƒêƒÉng nh·∫≠p h·ªá th·ªëng</h2>
        <p className="auth-sub">Qu·∫£n l√Ω nh√¢n s·ª± HRM</p>

        <form onSubmit={handleLogin} className="auth-form">
          <input
            type="text"
            className="form-control form-control-lg mb-3"
            placeholder="T√™n ƒëƒÉng nh·∫≠p"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />

          <input
            type="password"
            className="form-control form-control-lg mb-3"
            placeholder="M·∫≠t kh·∫©u"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button className="btn btn-primary w-100 auth-btn" disabled={loading}>
            {loading ? "‚è≥ ƒêang ƒëƒÉng nh·∫≠p..." : "ƒêƒÉng nh·∫≠p"}
          </button>
        </form>

        <p className="auth-switch">
          Ch∆∞a c√≥ t√†i kho·∫£n?{" "}
          <span className="auth-link" onClick={() => navigate("/register")}>
            ƒêƒÉng k√Ω ngay
          </span>
        </p>
      </div>
    </div>
  );
}

export default Login;
