import React, { useContext } from "react";
import { Navigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

function Protected({ children, roles }) {
  const { user } = useContext(AuthContext);

  // üîë N·∫øu context ch∆∞a c√≥ user th√¨ fallback t·ª´ localStorage
  let currentUser = user;
  if (!currentUser) {
    const authUser = localStorage.getItem("authUser");
    if (authUser) {
      try {
        const parsed = JSON.parse(authUser);
        currentUser = parsed.user;
      } catch (err) {
        console.error("‚ùå L·ªói parse authUser:", err);
      }
    }
  }

  // üö´ N·∫øu kh√¥ng c√≥ user sau khi fallback ‚Üí quay v·ªÅ login
  if (!currentUser) return <Navigate to="/" replace />;

  // üß≠ N·∫øu c√≥ roles v√† kh√¥ng kh·ªõp role c·ªßa user ‚Üí c·∫•m truy c·∫≠p
  if (roles && !roles.includes(currentUser.role)) {
    return <Navigate to="/" replace />;
  }

  return children;
}

export default Protected;
