import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function DocumentManagementAdmin() {
  const [docs, setDocs] = useState([]);
  const [folders, setFolders] = useState([]); // 📂 Danh sách thư mục
  const [form, setForm] = useState({
    folder: "",
    department: "general",
    files: [],
  });
  const [newFolder, setNewFolder] = useState("");

  // 📦 Tải danh sách tài liệu
  const load = async () => {
    try {
      const res = await api.get("/admin/documents");
      const allDocs = res.data;
      setDocs(allDocs);

      // 📂 Lọc ra thư mục thật sự
      const folderDocs = allDocs.filter((d) => d.isFolder);
      setFolders(folderDocs);
    } catch (err) {
      console.error("❌ Lỗi tải danh sách tài liệu:", err);
      alert("❌ Lỗi tải danh sách tài liệu");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // 📁 ======= TẠO THƯ MỤC =======
  const handleCreateFolder = async (e) => {
    e.preventDefault();
    if (!newFolder.trim()) return alert("⚠️ Vui lòng nhập tên thư mục");
    try {
      await api.post("/admin/documents/folder", {
        folderName: newFolder.trim(),
        department: form.department || "general",
      });
      alert(`✅ Đã tạo thư mục '${newFolder}'`);
      setNewFolder("");
      load();
    } catch (err) {
      console.error("❌ Lỗi tạo thư mục:", err);
      alert("❌ Lỗi tạo thư mục");
    }
  };

  // 📤 ======= UPLOAD FILES =======
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.folder) return alert("⚠️ Vui lòng chọn thư mục");
    if (!form.files || form.files.length === 0) return alert("⚠️ Chưa chọn file");

    try {
      const fd = new FormData();
      fd.append("department", form.department);
      fd.append("folder", form.folder);
      for (let file of form.files) fd.append("file", file);

      await api.post("/admin/documents", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("✅ Upload thành công");
      setForm({ folder: "", department: "general", files: [] });
      load();
    } catch (err) {
      console.error("❌ Lỗi upload file:", err);
      alert("❌ Lỗi upload file");
    }
  };

  // 🗑️ ======= XÓA FILE =======
  const handleDelete = async (id) => {
    if (!window.confirm("Bạn có chắc muốn xóa tài liệu này?")) return;
    try {
      await api.delete(`/admin/documents/${id}`);
      load();
    } catch (err) {
      console.error("❌ Lỗi khi xóa file:", err);
      alert("❌ Lỗi khi xóa file");
    }
  };

  // ⬇️ ======= TẢI FILE =======
  const handleDownload = (id) => {
    window.open(`http://localhost:5000/api/admin/documents/download/${id}`, "_blank");
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="admin" />
        </div>

        <div className="col-9">
          <h3>📂 Quản lý tài liệu</h3>

          {/* ====== TẠO THƯ MỤC ====== */}
          <form onSubmit={handleCreateFolder} className="card p-3 mb-3">
            <h5>📁 Tạo thư mục mới</h5>
            <div className="d-flex">
              <input
                type="text"
                className="form-control me-2"
                placeholder="Tên thư mục..."
                value={newFolder}
                onChange={(e) => setNewFolder(e.target.value)}
              />
              <button className="btn btn-success">➕ Tạo</button>
            </div>
          </form>

          {/* ====== UPLOAD FILE ====== */}
          <form onSubmit={handleSubmit} className="card p-3 mb-3">
            <h5>📤 Upload tài liệu</h5>

            <div className="row">
              <div className="col-md-4 mb-2">
                <label>Phòng ban</label>
                <select
                  className="form-control"
                  value={form.department}
                  onChange={(e) =>
                    setForm({ ...form, department: e.target.value, folder: "" })
                  }
                >
                  <option value="general">Chung</option>
                  <option value="IT">IT</option>
                  <option value="HR">HR</option>
                  <option value="Finance">Finance</option>
                </select>
              </div>

              <div className="col-md-4 mb-2">
                <label>Thư mục</label>
                <select
                  className="form-control"
                  value={form.folder}
                  onChange={(e) => setForm({ ...form, folder: e.target.value })}
                >
                  <option value="">-- Chọn thư mục --</option>
                  {folders
                    .filter((f) => f.department === form.department)
                    .map((f) => (
                      <option key={f._id} value={f.title}>
                        {f.title}
                      </option>
                    ))}
                </select>
              </div>

              <div className="col-md-4 mb-2">
                <label>Chọn file</label>
                <input
                  type="file"
                  className="form-control"
                  multiple
                  onChange={(e) => setForm({ ...form, files: e.target.files })}
                />
              </div>
            </div>

            <button className="btn btn-primary mt-2">📤 Upload</button>
          </form>

          {/* ====== DANH SÁCH TÀI LIỆU ====== */}
          <div className="card p-3">
            <h5>📑 Danh sách tài liệu</h5>
            {docs.filter((d) => !d.isFolder).length === 0 ? (
              <p>Chưa có tài liệu nào.</p>
            ) : (
              <table className="table table-bordered mt-2">
                <thead>
                  <tr>
                    <th>Phòng ban</th>       {/* 🏢 Thay “Thư mục” */}
                    <th>Thư mục</th>         {/* 📁 Thay “Tiêu đề” */}
                    <th>File tài liệu</th>   {/* 📄 Thay “Phòng ban” */}
                    <th>Người tải lên</th>
                    <th>Hành động</th>
                  </tr>
                </thead>
                <tbody>
                  {docs
                    .filter((d) => !d.isFolder)
                    .map((d) => (
                      <tr key={d._id}>
                        <td>{d.department}</td>             {/* phòng ban */}
                        <td>{d.folder || "Chung"}</td>      {/* thư mục */}
                        <td>{d.title}</td>                  {/* file */}
                        <td>{d.uploadedBy?.username}</td>
                        <td>
                          <button
                            className="btn btn-sm btn-success me-2"
                            onClick={() => handleDownload(d._id)}
                          >
                            ⬇️ Tải
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            onClick={() => handleDelete(d._id)}
                          >
                            🗑 Xóa
                          </button>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default DocumentManagementAdmin;
