import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function PerformanceReviewAdmin() {
  const [reviews, setReviews] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [editId, setEditId] = useState(null);

  const [form, setForm] = useState({
    userId: "",
    period: "",
    criteria: [
      { name: "Ch·∫•t l∆∞·ª£ng c√¥ng vi·ªác", score: 0, comment: "" },
      { name: "Th√°i ƒë·ªô & tinh th·∫ßn", score: 0, comment: "" },
      { name: "K·ªπ nƒÉng giao ti·∫øp", score: 0, comment: "" },
      { name: "K·ªπ nƒÉng k·ªπ thu·∫≠t", score: 0, comment: "" },
    ],
    strengths: "",
    improvements: "",
    goals: "",
  });

  // Load danh s√°ch ƒë√°nh gi√° + nh√¢n vi√™n
  const load = async () => {
    try {
      const res = await api.get("/admin/performance");
      setReviews(res.data);
      const empRes = await api.get("/admin/employees");
      setEmployees(empRes.data);
    } catch (err) {
      console.error("‚ùå API Error:", err);
      alert("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // Th√™m ho·∫∑c c·∫≠p nh·∫≠t
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await api.put(`/admin/performance/${editId}`, form);
        alert("‚úÖ ƒê√£ c·∫≠p nh·∫≠t ƒë√°nh gi√°");
      } else {
        await api.post("/admin/performance", form);
        alert("‚úÖ ƒê√£ th√™m ƒë√°nh gi√°");
      }
      setForm({
        userId: "",
        period: "",
        criteria: form.criteria.map(c => ({ ...c, score: 0, comment: "" })),
        strengths: "",
        improvements: "",
        goals: "",
      });
      setEditId(null);
      load();
    } catch (err) {
      console.error("‚ùå L·ªói th√™m/c·∫≠p nh·∫≠t:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng th·ªÉ l∆∞u ƒë√°nh gi√°");
    }
  };

  // X√≥a ƒë√°nh gi√°
  const handleDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a ƒë√°nh gi√° n√†y?")) return;
    try {
      await api.delete(`/admin/performance/${id}`);
      alert("üóëÔ∏è ƒê√£ x√≥a ƒë√°nh gi√°");
      load();
    } catch (err) {
      console.error("‚ùå L·ªói x√≥a:", err);
      alert("‚ùå Kh√¥ng th·ªÉ x√≥a ƒë√°nh gi√°");
    }
  };

  // S·ª≠a ƒë√°nh gi√°
  const startEdit = (r) => {
    setEditId(r._id);
    setForm({
      userId: r.userId?._id || "",
      period: r.period || "",
      criteria: r.criteria || form.criteria,
      strengths: r.strengths || "",
      improvements: r.improvements || "",
      goals: r.goals || "",
    });
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üìä Qu·∫£n l√Ω ƒë√°nh gi√° hi·ªáu su·∫•t</h3>

          {/* Form th√™m/s·ª≠a */}
          <form className="card p-3 mb-3" onSubmit={handleSubmit}>
            <div className="mb-2">
              <label>Nh√¢n vi√™n</label>
              <select className="form-control"
                value={form.userId}
                onChange={(e) => setForm({ ...form, userId: e.target.value })}
                required
              >
                <option value="">-- Ch·ªçn nh√¢n vi√™n --</option>
                {employees.map(emp => (
                  <option key={emp._id} value={emp.userId?._id}>
                    {emp.name} ({emp.userId?.username})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-2">
              <label>K·ª≥ ƒë√°nh gi√°</label>
              <input
                type="text"
                className="form-control"
                placeholder="VD: Q1-2025"
                value={form.period}
                onChange={(e) => setForm({ ...form, period: e.target.value })}
                required
              />
            </div>

            <h6>Ti√™u ch√≠ ƒë√°nh gi√°</h6>
            {form.criteria.map((c, i) => (
              <div key={i} className="mb-2">
                <label>{c.name}</label>
                <input
                  type="number"
                  min="1" max="5"
                  className="form-control mb-1"
                  value={c.score}
                  onChange={(e) => {
                    const updated = [...form.criteria];
                    updated[i].score = Number(e.target.value);
                    setForm({ ...form, criteria: updated });
                  }}
                />
                <textarea
                  className="form-control"
                  placeholder="Nh·∫≠n x√©t..."
                  value={c.comment}
                  onChange={(e) => {
