import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function PerformanceReviewAdmin() {
  const [reviews, setReviews] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [editId, setEditId] = useState(null);

  const [form, setForm] = useState({
    userId: "",
    period: "",
    criteria: [
      { name: "Chất lượng công việc", score: 0, comment: "" },
      { name: "Thái độ & tinh thần", score: 0, comment: "" },
      { name: "Kỹ năng giao tiếp", score: 0, comment: "" },
      { name: "Kỹ năng kỹ thuật", score: 0, comment: "" },
    ],
    strengths: "",
    improvements: "",
    goals: "",
  });

  // Load danh sách đánh giá + nhân viên
  const load = async () => {
    try {
      const res = await api.get("/admin/performance");
      setReviews(res.data);
      const empRes = await api.get("/admin/employees");
      setEmployees(empRes.data);
    } catch (err) {
      console.error("❌ API Error:", err);
      alert("❌ Không tải được dữ liệu");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // Thêm hoặc cập nhật
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await api.put(`/admin/performance/${editId}`, form);
        alert("✅ Đã cập nhật đánh giá");
      } else {
        await api.post("/admin/performance", form);
        alert("✅ Đã thêm đánh giá");
      }
      setForm({
        userId: "",
        period: "",
        criteria: form.criteria.map(c => ({ ...c, score: 0, comment: "" })),
        strengths: "",
        improvements: "",
        goals: "",
      });
      setEditId(null);
      load();
    } catch (err) {
      console.error("❌ Lỗi thêm/cập nhật:", err.response?.data || err.message);
      alert("❌ Không thể lưu đánh giá");
    }
  };

  // Xóa đánh giá
  const handleDelete = async (id) => {
    if (!window.confirm("Bạn có chắc muốn xóa đánh giá này?")) return;
    try {
      await api.delete(`/admin/performance/${id}`);
      alert("🗑️ Đã xóa đánh giá");
      load();
    } catch (err) {
      console.error("❌ Lỗi xóa:", err);
      alert("❌ Không thể xóa đánh giá");
    }
  };

  // Sửa đánh giá
  const startEdit = (r) => {
    setEditId(r._id);
    setForm({
      userId: r.userId?._id || "",
      period: r.period || "",
      criteria: r.criteria || form.criteria,
      strengths: r.strengths || "",
      improvements: r.improvements || "",
      goals: r.goals || "",
    });
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>📊 Quản lý đánh giá hiệu suất</h3>

          {/* Form thêm/sửa */}
          <form className="card p-3 mb-3" onSubmit={handleSubmit}>
            <div className="mb-2">
              <label>Nhân viên</label>
              <select className="form-control"
                value={form.userId}
                onChange={(e) => setForm({ ...form, userId: e.target.value })}
                required
              >
                <option value="">-- Chọn nhân viên --</option>
                {employees.map(emp => (
                  <option key={emp._id} value={emp.userId?._id}>
                    {emp.name} ({emp.userId?.username})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-2">
              <label>Kỳ đánh giá</label>
              <input
                type="text"
                className="form-control"
                placeholder="VD: Q1-2025"
                value={form.period}
                onChange={(e) => setForm({ ...form, period: e.target.value })}
                required
              />
            </div>

            <h6>Tiêu chí đánh giá</h6>
            {form.criteria.map((c, i) => (
              <div key={i} className="mb-2">
                <label>{c.name}</label>
                <input
                  type="number"
                  min="1" max="5"
                  className="form-control mb-1"
                  value={c.score}
                  onChange={(e) => {
                    const updated = [...form.criteria];
                    updated[i].score = Number(e.target.value);
                    setForm({ ...form, criteria: updated });
                  }}
                />
                <textarea
                  className="form-control"
                  placeholder="Nhận xét..."
                  value={c.comment}
                  onChange={(e) => {
                    const updated = [...form.criteria];
                    updated[i].comment = e.target.value;
                    setForm({ ...form, criteria: updated });
                  }}
                />
              </div>
            ))}

            <div className="mb-2">
              <label>Điểm mạnh</label>
              <textarea className="form-control"
                value={form.strengths}
                onChange={(e) => setForm({ ...form, strengths: e.target.value })}
              />
            </div>
            <div className="mb-2">
              <label>Cần cải thiện</label>
              <textarea className="form-control"
                value={form.improvements}
                onChange={(e) => setForm({ ...form, improvements: e.target.value })}
              />
            </div>
            <div className="mb-2">
              <label>Mục tiêu kỳ sau</label>
              <textarea className="form-control"
                value={form.goals}
                onChange={(e) => setForm({ ...form, goals: e.target.value })}
              />
            </div>

            <button className="btn btn-primary">
              {editId ? "💾 Lưu thay đổi" : "➕ Thêm đánh giá"}
            </button>
            {editId && (
              <button
                type="button"
                className="btn btn-secondary ms-2"
                onClick={() => {
                  setEditId(null);
                  setForm({
                    userId: "",
                    period: "",
                    criteria: form.criteria.map(c => ({ ...c, score: 0, comment: "" })),
                    strengths: "",
                    improvements: "",
                    goals: "",
                  });
                }}
              >
                Hủy
              </button>
            )}
          </form>

          {/* Danh sách đánh giá */}
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Nhân viên</th>
                <th>Kỳ</th>
                <th>Điểm TB</th>
                <th>Điểm mạnh</th>
                <th>Cần cải thiện</th>
                <th>Mục tiêu</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {reviews.map(r => (
                <tr key={r._id}>
                  <td>{r.userId?.username}</td>
                  <td>{r.period}</td>
                  <td>{r.overallScore?.toFixed(1)}</td>
                  <td>{r.strengths}</td>
                  <td>{r.improvements}</td>
                  <td>{r.goals}</td>
                  <td>
                    <button className="btn btn-warning btn-sm me-2" onClick={() => startEdit(r)}>Sửa</button>
                    <button className="btn btn-danger btn-sm" onClick={() => handleDelete(r._id)}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

        </div>
      </div>
    </div>
  );
}

export default PerformanceReviewAdmin;
