import React, { useEffect, useState } from "react";
import AdminLayout from "../../layouts/AdminLayout";
import api from "../../api";

export default function AttendanceManagementAdmin() {
  const [list, setList] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [checked, setChecked] = useState([]);
  const [selectedDate, setSelectedDate] = useState("");

  // ==== Popup Edit ====
  const [editing, setEditing] = useState(null);
  const [editData, setEditData] = useState({
    checkIn: "",
    checkOut: "",
    lateMinutes: 0,
    overtimeHours: 0,
    totalDays: 1,
  });

  // ======================
  //  H√ÄM X·ª¨ L√ù T√çNH TO√ÅN
  // ======================

  // T√≠nh s·ªë ph√∫t tr·ªÖ t·ª´ gi·ªù v√†o
  const calcLate = (checkInStr) => {
    if (!checkInStr) return 0;
    const d = new Date(checkInStr);
    const mins = d.getHours() * 60 + d.getMinutes();
    const late = mins - 7 * 60;
    return late > 0 ? late : 0;
  };

  // T·∫°o gi·ªù v√†o m·ªõi t·ª´ ph√∫t tr·ªÖ
  const calcCheckInFromLate = (dateStr, late) => {
    if (!dateStr) return "";
    if (late < 0) late = 0;

    const base = new Date(dateStr);
    base.setHours(7, 0, 0, 0); // gi·ªù chu·∫©n 7:00
    base.setMinutes(base.getMinutes() + late);

    return base.toISOString().slice(0, 16);
  };

  // T√≠nh OT t·ª´ gi·ªù ra
  const calcOT = (checkOutStr) => {
    if (!checkOutStr) return 0;
    const d = new Date(checkOutStr);
    const mins = d.getHours() * 60 + d.getMinutes();
    const diff = mins - 17 * 60;
    return diff > 0 ? (diff / 60).toFixed(2) : 0;
  };

  const load = async () => {
    try {
      const params = {};
      if (selectedDate) params.date = selectedDate;

      const res = await api.get("/attendance", { params });
      setList(res.data);

      const emp = await api.get("/admin/employees");
      setEmployees(
        emp.data.sort((a, b) =>
          (a.userId?.username || "").localeCompare(b.userId?.username || "")
        )
      );
    } catch (err) {
      alert("Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu");
    }
  };

  useEffect(() => {
    load();
  }, [selectedDate]);

  const fmtTime = (t) =>
    t ? new Date(t).toLocaleTimeString("vi-VN", { hour12: false }) : "‚Äì";

  const fmtDate = (d) =>
    d ? new Date(d).toLocaleDateString("vi-VN") : "‚Äì";

  const toggleCheck = (uid) => {
    setChecked((p) =>
      p.includes(uid) ? p.filter((x) => x !== uid) : [...p, uid]
    );
  };

  // ======================
  // CH·∫§M C√îNG NHANH
  // ======================
  const bulkCheckIn = async () => {
    if (checked.length === 0) return alert("Ch∆∞a ch·ªçn nh√¢n vi√™n");

    const today = new Date().toISOString().split("T")[0];

    if (selectedDate && selectedDate !== today) {
      const c = window.confirm(
        "B·∫°n ƒëang xem ng√†y kh√°c h√¥m nay. Ch·∫•m c√¥ng nhanh lu√¥n t√≠nh cho NG√ÄY H√îM NAY. Ti·∫øp t·ª•c?"
      );
      if (!c) return;
    }

    if (!window.confirm(`Ch·∫•m c√¥ng ngay cho ${checked.length} nh√¢n vi√™n?`))
      return;

    try {
      await api.post("/attendance/bulk-checkin", { userIds: checked });

      alert("‚úî ƒê√£ ch·∫•m c√¥ng!");
      setChecked([]);
      setSelectedDate(today);
    } catch (err) {
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ ch·∫•m c√¥ng");
    }
  };

  // ======================
  // XO√Å
  // ======================
  const remove = async (id) => {
    if (!window.confirm("Xo√° b·∫£n ghi n√†y?")) return;

    await api.delete(`/attendance/manual/${id}`);
    load();
  };

  // ======================
  // M·ªû POPUP EDIT
  // ======================
  const openEdit = (r) => {
    setEditing(r);

    setEditData({
      checkIn: r.checkIn
        ? new Date(r.checkIn).toISOString().slice(0, 16)
        : "",
      checkOut: r.checkOut
        ? new Date(r.checkOut).toISOString().slice(0, 16)
        : "",
      lateMinutes: r.lateMinutes,
      overtimeHours: r.overtimeHours,
      totalDays: r.totalDays,
      date: r.date, // c·∫ßn ƒë·ªÉ t√≠nh l·∫°i gi·ªù v√†o
    });
  };

  // ======================
  // L∆ØU S·ª¨A
  // ======================
  const saveEdit = async () => {
    try {
      await api.put(`/attendance/manual/${editing._id}`, editData);
      alert("‚úî ƒê√£ c·∫≠p nh·∫≠t!");
      setEditing(null);
      load();
    } catch (err) {
      alert("Kh√¥ng th·ªÉ l∆∞u thay ƒë·ªïi");
    }
  };

  // ======================
  // B·∫¢NG D·ªÆ LI·ªÜU
  // ======================
  const renderBody = () => {
    if (selectedDate) {
      if (list.length === 0)
        return (
          <tr>
            <td colSpan={10} className="text-center text-muted">
              Kh√¥ng ai ch·∫•m c√¥ng ng√†y n√†y
            </td>
          </tr>
        );

      return list.map((r) => (
        <tr key={r._id}>
          <td>
            <input
              type="checkbox"
              checked={checked.includes(r.userId?._id)}
              onChange={() => toggleCheck(r.userId?._id)}
            />
          </td>
          <td>{r.userId?.username}</td>
          <td>{fmtDate(r.date)}</td>
          <td>{fmtTime(r.checkIn)}</td>
          <td>{fmtTime(r.checkOut)}</td>
          <td>{r.lateMinutes}</td>
          <td>{r.overtimeHours}</td>
          <td>{r.totalDays}</td>
          <td className="text-nowrap">
            <button
              className="btn btn-sm btn-warning me-2"
              onClick={() => openEdit(r)}
            >
              ‚úèÔ∏è S·ª≠a
            </button>
            <button
              className="btn btn-sm btn-danger"
              onClick={() => remove(r._id)}
            >
              üóë Xo√°
            </button>
          </td>
        </tr>
      ));
    }

    // ‚ö° Mode "Xem t·∫•t c·∫£"
    return employees.map((emp) => {
      const rec = list.find((x) => x.userId?._id === emp.userId?._id);

      return (
        <tr key={emp._id}>
          <td>
            <input
              type="checkbox"
              checked={checked.includes(emp.userId?._id)}
              onChange={() => toggleCheck(emp.userId?._id)}
            />
          </td>
          <td>{emp.userId?.username}</td>
          <td>{fmtDate(rec?.date)}</td>
          <td>{fmtTime(rec?.checkIn)}</td>
          <td>{fmtTime(rec?.checkOut)}</td>
          <td>{rec?.lateMinutes ?? 0}</td>
          <td>{rec?.overtimeHours ?? 0}</td>
          <td>{rec?.totalDays ?? 0}</td>
          <td className="text-nowrap">
            {rec?._id && (
              <>
                <button
                  className="btn btn-sm btn-warning me-2"
                  onClick={() => openEdit(rec)}
                >
                  ‚úèÔ∏è S·ª≠a
                </button>
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => remove(rec._id)}
                >
                  üóë Xo√°
                </button>
              </>
            )}
          </td>
        </tr>
      );
    });
  };

  return (
    <AdminLayout>
      <h2 className="mb-3">‚è± Qu·∫£n l√Ω ch·∫•m c√¥ng</h2>

      {/* B·ªô l·ªçc ng√†y */}
      <div className="d-flex align-items-center gap-3 mb-3">
        <div>
          <label className="fw-bold me-2">üìÖ Xem ng√†y:</label>
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            className="form-control d-inline-block"
            style={{ width: 180 }}
          />
        </div>

        <button
          className="btn btn-outline-primary"
          onClick={() =>
            setSelectedDate(new Date().toISOString().split("T")[0])
          }
        >
          H√¥m nay
        </button>

        <button
          className="btn btn-secondary"
          onClick={() => setSelectedDate("")}
        >
          Xem t·∫•t c·∫£
        </button>
      </div>

      {/* Ch·∫•m c√¥ng nhanh */}
      <div className="d-flex gap-2 mb-3">
        <button
          className="btn btn-success"
          disabled={checked.length === 0}
          onClick={bulkCheckIn}
        >
          ‚úî Ch·∫•m c√¥ng ngay ({checked.length})
        </button>

        <button className="btn btn-secondary" onClick={() => setChecked([])}>
          B·ªè ch·ªçn
        </button>
      </div>

      {/* B·∫£ng */}
      <div className="card p-3">
        <table className="table table-bordered table-striped">
          <thead>
            <tr>
              <th></th>
              <th>Nh√¢n vi√™n</th>
              <th>Ng√†y</th>
              <th>Gi·ªù v√†o</th>
              <th>Ra</th>
              <th>Tr·ªÖ (ph√∫t)</th>
              <th>OT (gi·ªù)</th>
              <th>C√¥ng</th>
              <th></th>
            </tr>
          </thead>
          <tbody>{renderBody()}</tbody>
        </table>
      </div>

      {/* POPUP EDIT */}
      {editing && (
        <div
          style={{
            position: "fixed",
            inset: 0,
            background: "rgba(0,0,0,0.4)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 9999,
          }}
        >
          <div className="card p-4" style={{ width: 420 }}>
            <h4 className="mb-3">‚úèÔ∏è S·ª≠a ch·∫•m c√¥ng</h4>

            {/* GI·ªú V√ÄO */}
            <label>Gi·ªù v√†o</label>
            <input
              type="datetime-local"
              className="form-control mb-2"
              value={editData.checkIn}
              onChange={(e) => {
                const v = e.target.value;
                setEditData({
                  ...editData,
                  checkIn: v,
                  lateMinutes: calcLate(v),
                });
              }}
            />

            {/* GI·ªú RA */}
            <label>Gi·ªù ra</label>
            <input
              type="datetime-local"
              className="form-control mb-2"
              value={editData.checkOut}
              onChange={(e) => {
                const v = e.target.value;
                setEditData({
                  ...editData,
                  checkOut: v,
                  overtimeHours: calcOT(v),
                });
              }}
            />

            {/* TR·ªÑ */}
            <label>ƒêi tr·ªÖ (ph√∫t)</label>
            <input
              type="number"
              className="form-control mb-2"
              value={editData.lateMinutes}
              onChange={(e) => {
                const lm = Number(e.target.value);
                setEditData({
                  ...editData,
                  lateMinutes: lm,
                  checkIn: calcCheckInFromLate(editData.date, lm),
                });
              }}
            />

            {/* OT */}
            <label>OT (gi·ªù)</label>
            <input
              disabled
              type="number"
              className="form-control mb-2"
              value={editData.overtimeHours}
              readOnly
            />

            {/* C√îNG */}
            <label>C√¥ng</label>
            <select
              className="form-control mb-3"
              value={editData.totalDays}
              onChange={(e) =>
                setEditData({ ...editData, totalDays: Number(e.target.value) })
              }
            >
              <option value={0}>0 ‚Äî Kh√¥ng c√¥ng</option>
              <option value={1}>1 ‚Äî ƒê·ªß c√¥ng</option>
            </select>

            <div className="d-flex justify-content-end gap-2">
              <button
                className="btn btn-secondary"
                onClick={() => setEditing(null)}
              >
                Hu·ª∑
              </button>
              <button className="btn btn-primary" onClick={saveEdit}>
                L∆∞u thay ƒë·ªïi
              </button>
            </div>
          </div>
        </div>
      )}
    </AdminLayout>
  );
}
