import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function SalaryManagement({ role }) {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({ userId: "", amount: "", month: "" });

  const load = async () => {
    if (role === "admin") {
      const res = await api.get("/admin/salary");
      setList(res.data);
    } else {
      // TODO: Backend chưa có API lương cho user -> cần bổ sung sau
      setList([]);
    }
  };
  useEffect(() => { load(); }, [role]);

  const add = async (e) => {
    e.preventDefault();
    if (role !== "admin") return;
    await api.post("/admin/salary", form);
    setForm({ userId: "", amount: "", month: "" });
    load();
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role={role==="admin"?"admin":"user"} /></div>
        <div className="col-9">
          <h3>💰 Quản lý lương ({role})</h3>

          {role === "admin" && (
            <form onSubmit={add} className="mb-3">
              <div className="row g-2">
                <div className="col"><input className="form-control" placeholder="UserID" value={form.userId} onChange={e=>setForm({...form,userId:e.target.value})} /></div>
                <div className="col"><input className="form-control" placeholder="Số tiền" value={form.amount} onChange={e=>setForm({...form,amount:e.target.value})} /></div>
                <div className="col"><input className="form-control" placeholder="Tháng (YYYY-MM)" value={form.month} onChange={e=>setForm({...form,month:e.target.value})} /></div>
                <div className="col"><button className="btn btn-primary w-100">Thêm</button></div>
              </div>
            </form>
          )}

          <table className="table table-bordered">
            <thead><tr><th>User</th><th>Số tiền</th><th>Tháng</th></tr></thead>
            <tbody>
              {list.map(s => (
                <tr key={s._id}>
                  <td>{s.userId?.username}</td>
                  <td>{s.amount}</td>
                  <td>{s.month}</td>
                </tr>
              ))}
            </tbody>
          </table>

          {role !== "admin" && <p>TODO: Backend chưa có API xem lương cá nhân.</p>}
        </div>
      </div>
    </div>
  );
}
export default SalaryManagement;
