// frontend/src/pages/admin/NotificationsAdmin.js
import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function NotificationsAdmin() {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({ title: "", message: "" });

  const load = async () => {
    try {
      const res = await api.get("/admin/notifications");
      setList(res.data);
    } catch (err) {
      console.error(err);
      alert("‚ùå L·ªói t·∫£i th√¥ng b√°o");
    }
  };

  useEffect(() => { load(); }, []);

  const send = async () => {
    try {
      await api.post("/admin/notifications", form);
      setForm({ title: "", message: "" });
      load();
      alert("‚úÖ G·ª≠i th√¥ng b√°o th√†nh c√¥ng");
    } catch (err) {
      console.error(err);
      alert("‚ùå L·ªói g·ª≠i th√¥ng b√°o");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üîî Qu·∫£n l√Ω th√¥ng b√°o (Admin)</h3>
          <div className="mb-3">
            <input
              className="form-control mb-2"
              placeholder="Ti√™u ƒë·ªÅ"
              value={form.title}
              onChange={(e) => setForm({ ...form, title: e.target.value })}
            />
            <textarea
              className="form-control mb-2"
              placeholder="N·ªôi dung"
              value={form.message}
              onChange={(e) => setForm({ ...form, message: e.target.value })}
            />
            <button className="btn btn-primary" onClick={send}>G·ª≠i th√¥ng b√°o</button>
          </div>

          <ul className="list-group">
            {list.map((n) => (
              <li key={n._id} className="list-group-item">
                <strong>{n.title}</strong> - {n.message}
                <span className="text-muted"> ({new Date(n.createdAt).toLocaleString()})</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default NotificationsAdmin;
