import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function SalaryManagementAdmin() {
  const [salaries, setSalaries] = useState([]);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({ dailyRate: "", overtimeRate: "" });

  const load = async () => {
    try {
      const res = await api.get("/admin/salary");
      setSalaries(res.data);
    } catch (err) {
      console.error("❌ API Error:", err.response?.data || err.message);
      alert("❌ Không tải được dữ liệu lương");
    }
  };

  useEffect(() => {
    load();
  }, []);

  const startEdit = (s) => {
    setEditId(s._id);
    setForm({ dailyRate: s.dailyRate, overtimeRate: s.overtimeRate || 50000 });
  };

  const saveEdit = async (id) => {
    try {
      await api.put(`/admin/salary/${id}`, form);
      alert("✅ Cập nhật lương thành công");
      setEditId(null);
      setForm({ dailyRate: "", overtimeRate: "" });
      load();
    } catch (err) {
      console.error("❌ Update Error:", err.response?.data || err.message);
      alert("❌ Lỗi cập nhật lương");
    }
  };

  const cancelEdit = () => {
    setEditId(null);
    setForm({ dailyRate: "", overtimeRate: "" });
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>💰 Quản lý lương (Admin)</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Nhân viên</th>
                <th>Ngày công</th>
                <th>Phạt (VND)</th>
                <th>Tăng ca (giờ)</th>
                <th>Tiền ngày (VND)</th>
                <th>Tiền tăng ca (VND/giờ)</th>
                <th>Tổng lương (VND)</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {salaries.map((s) => (
                <tr key={s._id}>
                  <td>{s.name} ({s.userId?.username})</td>
                  <td>{s.totalDays}</td>
                  <td>{s.penalty?.toLocaleString("vi-VN")}</td>
                  <td>{s.overtimeHours}</td>
                  <td>
                    {editId === s._id ? (
                      <input
                        className="form-control"
                        value={form.dailyRate}
                        onChange={(e) => setForm({ ...form, dailyRate: e.target.value })}
                      />
                    ) : (
                      s.dailyRate.toLocaleString("vi-VN")
                    )}
                  </td>
                  <td>
                    {editId === s._id ? (
                      <input
                        className="form-control"
                        value={form.overtimeRate}
                        onChange={(e) => setForm({ ...form, overtimeRate: e.target.value })}
                      />
                    ) : (
                      s.overtimeRate?.toLocaleString("vi-VN")
                    )}
                  </td>
                  <td>{s.amount.toLocaleString("vi-VN")}</td>
                  <td>
                    {editId === s._id ? (
                      <>
                        <button className="btn btn-success btn-sm me-2" onClick={() => saveEdit(s._id)}>Lưu</button>
                        <button className="btn btn-secondary btn-sm" onClick={cancelEdit}>Hủy</button>
                      </>
                    ) : (
                      <button className="btn btn-warning btn-sm" onClick={() => startEdit(s)}>Sửa</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default SalaryManagementAdmin;
