import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

export default function DocumentManagementUser() {
  const [docs, setDocs] = useState([]);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [loading, setLoading] = useState(false);
  const [currentUserId, setCurrentUserId] = useState(null);
  const [currentRole, setCurrentRole] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const decoded = JSON.parse(atob(token.split(".")[1]));
        setCurrentUserId(decoded?.id);
        setCurrentRole(decoded?.role);
      } catch (err) {
        console.error("‚ùå L·ªói gi·∫£i m√£ token:", err);
      }
    }
  }, []);

  const load = async () => {
    try {
      const res = await api.get("/employees/documents");
      setDocs(res.data || []);
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i danh s√°ch t√†i li·ªáu:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ t·∫£i danh s√°ch t√†i li·ªáu!");
    }
  };

  useEffect(() => {
    load();
  }, []);

  const downloadFile = async (id) => {
    try {
      const response = await api.get(`/employees/documents/download/${id}`, {
        responseType: "blob",
      });

      const disposition = response.headers["content-disposition"];
      let filename = "tai-lieu";
      if (disposition) {
        const utf8Match = disposition.match(/filename\*\=UTF-8''(.+)/);
        const normalMatch = disposition.match(/filename="(.+)"/);
        if (utf8Match && utf8Match[1]) {
          filename = decodeURIComponent(utf8Match[1]);
        } else if (normalMatch && normalMatch[1]) {
          filename = decodeURIComponent(normalMatch[1]);
        }
      }

      const contentType = response.headers["content-type"] || "application/octet-stream";
      const blob = new Blob([response.data], { type: contentType });
      const fileURL = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = fileURL;
      link.setAttribute("download", filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(fileURL);
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i file:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ t·∫£i t√†i li·ªáu!");
    }
  };

  const uploadFile = async (e) => {
    e.preventDefault();
    if (!file || !title.trim()) return alert("Vui l√≤ng nh·∫≠p ti√™u ƒë·ªÅ v√† ch·ªçn file!");

    const formData = new FormData();
    formData.append("title", title);
    formData.append("file", file);

    try {
      setLoading(true);
      await api.post("/employees/documents", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("‚úÖ T·∫£i t√†i li·ªáu l√™n th√†nh c√¥ng!");
      setFile(null);
      setTitle("");
      await load();
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i l√™n:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ t·∫£i l√™n t√†i li·ªáu!");
    } finally {
      setLoading(false);
    }
  };

  const deleteFile = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a t√†i li·ªáu n√†y kh√¥ng?")) return;
    try {
      await api.delete(`/employees/documents/${id}`);
      alert("üóëÔ∏è ƒê√£ x√≥a t√†i li·ªáu");
      await load();
    } catch (err) {
      console.error("‚ùå L·ªói x√≥a t√†i li·ªáu:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ x√≥a t√†i li·ªáu!");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="user" />
        </div>
        <div className="col-9">
          <h3>üìÇ T√†i li·ªáu ph√≤ng ban c·ªßa t√¥i</h3>

          <form className="card p-3 mb-3" onSubmit={uploadFile}>
            <h5 className="mb-3">‚¨ÜÔ∏è T·∫£i t√†i li·ªáu l√™n</h5>
            <input
              type="text"
              className="form-control mb-2"
              placeholder="Nh·∫≠p ti√™u ƒë·ªÅ t√†i li·ªáu..."
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
            <input
              type="file"
              className="form-control mb-2"
              onChange={(e) => setFile(e.target.files[0])}
              required
            />
            <button className="btn btn-primary" disabled={loading}>
              {loading ? "ƒêang t·∫£i l√™n..." : "üì§ T·∫£i l√™n"}
            </button>
          </form>

          <div className="card p-3">
            <h5>üìë Danh s√°ch t√†i li·ªáu</h5>
            {docs.length === 0 ? (
              <p>Ch∆∞a c√≥ t√†i li·ªáu n√†o.</p>
            ) : (
              <table className="table table-bordered table-hover mt-2">
                <thead className="table-light">
                  <tr>
                    <th>üè¢ Ph√≤ng ban</th>
                    <th>üìÅ Th∆∞ m·ª•c</th>
                    <th>üìÑ File t√†i li·ªáu</th>
                    <th>üë§ Ng∆∞·ªùi t·∫£i l√™n</th>
                    <th className="text-center">‚öôÔ∏è H√†nh ƒë·ªông</th>
                  </tr>
                </thead>
                <tbody>
                  {docs.map((d) => (
                    <tr key={d._id}>
                      <td>{d.department}</td>
                      <td>{d.folder || "‚Äî"}</td>
                      <td><i className="bi bi-file-earmark-text me-2"></i>{d.title}</td>
                      <td>{d.uploadedBy?.username || "Kh√¥ng r√µ"}</td>
                      <td className="text-center">
                        <button
                          className="btn btn-sm btn-success me-2"
                          onClick={() => downloadFile(d._id)}
                        >
                          ‚¨áÔ∏è T·∫£i
                        </button>
                       {(
  (d.uploadedBy && String(d.uploadedBy._id || d.uploadedBy) === String(currentUserId)) // ng∆∞·ªùi upload
  || currentRole === "admin" // admin
  || (d.department !== "general" && d.department === currentUserDept) // nh√¢n vi√™n c√πng ph√≤ng ban (tr·ª´ general)
) && (
  <button
    className="btn btn-sm btn-danger"
    onClick={() => deleteFile(d._id)}
  >
    üóëÔ∏è X√≥a
  </button>
)}

                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
