import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function PerformanceReviewAdmin() {
  const [reviews, setReviews] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [form, setForm] = useState({
    userId: "",
    tasksCompleted: 0,
    communication: 0,
    technical: 0,
    attitude: 10,
    feedback: "",
  });

  const load = async () => {
    try {
      const res = await api.get("/admin/performance");
      setReviews(res.data);
      const empRes = await api.get("/admin/employees");
      setEmployees(empRes.data);
    } catch (err) {
      console.error("❌ API Error:", err.response?.data || err.message);
    }
  };

  useEffect(() => {
    load();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post("/admin/performance", form);
      alert("✅ Đã thêm đánh giá");
      setForm({
        userId: "",
        tasksCompleted: 0,
        communication: 0,
        technical: 0,
        attitude: 10,
        feedback: "",
      });
      load();
    } catch (err) {
      console.error("❌ Lỗi thêm/cập nhật:", err.response?.data || err.message);
      alert("❌ Không thể thêm đánh giá");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="admin" />
        </div>
        <div className="col-9">
          <h3>📊 Đánh giá hiệu suất</h3>

          {/* Form thêm đánh giá */}
          <form className="card p-3 mb-3" onSubmit={handleSubmit}>
            {/* Nhân viên */}
            <label className="form-label">Nhân viên</label>
            <select
              className="form-control mb-2"
              value={form.userId}
              onChange={(e) => setForm({ ...form, userId: e.target.value })}
              required
            >
              <option value="">-- Chọn nhân viên --</option>
              {employees.map((emp) => (
                <option key={emp._id} value={emp.userId?._id}>
                  {emp.name} ({emp.userId?.username})
                </option>
              ))}
            </select>

            {/* Hoàn thành nhiệm vụ */}
            <label className="form-label">Hoàn thành nhiệm vụ (số lượng)</label>
            <input
              type="number"
              className="form-control mb-2"
              value={form.tasksCompleted}
              onChange={(e) =>
                setForm({ ...form, tasksCompleted: Number(e.target.value) })
              }
              min="0"
              required
            />

            {/* Giao tiếp */}
            <label className="form-label">Kỹ năng giao tiếp</label>
            <select
              className="form-control mb-2"
              value={form.communication}
              onChange={(e) =>
                setForm({ ...form, communication: Number(e.target.value) })
              }
            >
              <option value="0">Không biết (0 điểm)</option>
              <option value="2">Kém (2 điểm)</option>
              <option value="4">Có thể giao tiếp (4 điểm)</option>
              <option value="6">Khá (6 điểm)</option>
              <option value="8">Tốt (8 điểm)</option>
              <option value="10">Giỏi (10 điểm)</option>
            </select>

            {/* Kỹ thuật */}
            <label className="form-label">Kỹ năng kỹ thuật</label>
            <select
              className="form-control mb-2"
              value={form.technical}
              onChange={(e) =>
                setForm({ ...form, technical: Number(e.target.value) })
              }
            >
              <option value="0">Không biết (0 điểm)</option>
              <option value="2">Kém (2 điểm)</option>
              <option value="4">Cơ bản (4 điểm)</option>
              <option value="6">Khá (6 điểm)</option>
              <option value="8">Tốt (8 điểm)</option>
              <option value="10">Giỏi (10 điểm)</option>
            </select>

            {/* Thái độ */}
            <label className="form-label">Thái độ / tinh thần</label>
            <select
              className="form-control mb-2"
              value={form.attitude}
              onChange={(e) =>
                setForm({ ...form, attitude: Number(e.target.value) })
              }
            >
              <option value="0">Tiêu cực (0 điểm)</option>
              <option value="10">Tích cực (10 điểm)</option>
            </select>

            {/* Nhận xét */}
            <label className="form-label">Nhận xét</label>
            <textarea
              className="form-control mb-2"
              value={form.feedback}
              onChange={(e) => setForm({ ...form, feedback: e.target.value })}
            />

            <button className="btn btn-primary">➕ Thêm đánh giá</button>
          </form>

          {/* Danh sách đánh giá */}
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Nhân viên</th>
                <th>Nhiệm vụ hoàn thành</th>
                <th>Giao tiếp</th>
                <th>Kỹ thuật</th>
                <th>Thái độ</th>
                <th>Nhận xét</th>
              </tr>
            </thead>
            <tbody>
              {reviews.map((r) => (
                <tr key={r._id}>
                  <td>{r.userId?.username}</td>
                  <td>{r.tasksCompleted}</td>
                  <td>{r.communication}</td>
                  <td>{r.technical}</td>
                  <td>{r.attitude}</td>
                  <td>{r.feedback}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default PerformanceReviewAdmin;
