import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function DocumentManagementAdmin() {
  const [docs, setDocs] = useState([]);
  const [form, setForm] = useState({ title: "", department: "general", file: null });

  const load = async () => {
    try {
      const res = await api.get("/admin/documents");
      setDocs(res.data);
    } catch (err) {
      console.error("❌ Lỗi tải tài liệu:", err);
    }
  };

  useEffect(() => {
    load();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      fd.append("title", form.title);
      fd.append("department", form.department);
      fd.append("file", form.file);

      await api.post("/admin/documents", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("✅ Upload thành công");
      setForm({ title: "", department: "general", file: null });
      load();
    } catch {
      alert("❌ Lỗi upload");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Bạn có chắc muốn xóa tài liệu này?")) return;
    await api.delete(`/admin/documents/${id}`);
    load();
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>📂 Quản lý tài liệu</h3>

          {/* Upload form */}
          <form onSubmit={handleSubmit} className="mb-3">
            <input
              type="text"
              placeholder="Tiêu đề"
              className="form-control mb-2"
              value={form.title}
              onChange={(e) => setForm({ ...form, title: e.target.value })}
              required
            />
            <select
              className="form-control mb-2"
              value={form.department}
              onChange={(e) => setForm({ ...form, department: e.target.value })}
            >
              <option value="general">Chung</option>
              <option value="IT">IT</option>
              <option value="HR">HR</option>
              <option value="Finance">Finance</option>
            </select>
            <input
              type="file"
              className="form-control mb-2"
              onChange={(e) => setForm({ ...form, file: e.target.files[0] })}
              required
            />
            <button className="btn btn-primary">📤 Upload</button>
          </form>

          {/* Danh sách tài liệu */}
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Tiêu đề</th>
                <th>Phòng ban</th>
                <th>Người tải lên</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {docs.map((d) => (
                <tr key={d._id}>
                  <td>{d.title}</td>
                  <td>{d.department}</td>
                  <td>{d.uploadedBy?.username}</td>
                  <td>
                    {/* Chỉ có tải và xóa */}
                    <a
                      href={`http://localhost:5000/admin/documents/download/${d._id}`}
                      className="btn btn-sm btn-success me-2"
                    >
                      ⬇️ Tải
                    </a>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDelete(d._id)}
                    >
                      🗑️ Xóa
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default DocumentManagementAdmin;
