import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";
import { DEPARTMENTS, POSITIONS, ROLES } from "../../config/constants";

function HRManagement() {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({
    username: "",
    password: "",
    name: "",
    department: "",
    position: "",
    role: "employee",
  });
  const [editId, setEditId] = useState(null);

  const load = async () => {
    try {
      const res = await api.get("/admin/employees");
      setList(res.data);
    } catch (err) {
      alert("‚ùå L·ªói t·∫£i danh s√°ch nh√¢n vi√™n");
    }
  };

  useEffect(() => { load(); }, []);

  // Th√™m nh√¢n vi√™n
  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await api.put(`/admin/employees/${editId}`, form);
        alert("‚úÖ C·∫≠p nh·∫≠t nh√¢n vi√™n th√†nh c√¥ng");
      } else {
        await api.post("/admin/employees", form);
        alert("‚úÖ Th√™m nh√¢n vi√™n th√†nh c√¥ng");
      }
      setForm({ username: "", password: "", name: "", department: "", position: "", role: "employee" });
      setEditId(null);
      load();
    } catch (err) {
      alert(err.response?.data?.error || "‚ùå L·ªói");
    }
  };

  // Ch·ªânh s·ª≠a nh√¢n vi√™n
  const onEdit = (emp) => {
    setEditId(emp._id);
    setForm({
      username: emp.userId?.username,
      password: "", // kh√¥ng show password
      name: emp.name,
      department: emp.department,
      position: emp.position,
      role: emp.userId?.role,
    });
  };

  // X√≥a nh√¢n vi√™n
  const onDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën x√≥a nh√¢n vi√™n n√†y?")) return;
    try {
      await api.delete(`/admin/employees/${id}`);
      alert("üóëÔ∏è ƒê√£ x√≥a nh√¢n vi√™n");
      load();
    } catch (err) {
      alert("‚ùå L·ªói khi x√≥a");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üë®‚Äçüíº Qu·∫£n l√Ω nh√¢n s·ª±</h3>

          {/* Form th√™m/s·ª≠a nh√¢n vi√™n */}
          <form onSubmit={onSubmit} className="mb-4 border p-3 rounded">
            <h5>{editId ? "‚úèÔ∏è Ch·ªânh s·ª≠a nh√¢n vi√™n" : "‚ûï Th√™m nh√¢n vi√™n m·ªõi"}</h5>

            <div className="row g-2 mt-2">
              <div className="col">
                <input
                  className="form-control"
                  placeholder="Username"
                  value={form.username}
                  disabled={!!editId}
                  onChange={(e)=>setForm({...form, username: e.target.value})}
                  required
                />
              </div>
              {!editId && (
                <div className="col">
                  <input
                    className="form-control"
                    type="password"
                    placeholder="Password"
                    value={form.password}
                    onChange={(e)=>setForm({...form, password: e.target.value})}
                    required
                  />
                </div>
              )}
            </div>

            <div className="row g-2 mt-2">
              <div className="col">
                <input
                  className="form-control"
                  placeholder="H·ªç t√™n"
                  value={form.name}
                  onChange={(e)=>setForm({...form, name: e.target.value})}
                  required
                />
              </div>
              <div className="col">
                <select
                  className="form-control"
                  value={form.department}
                  onChange={(e)=>setForm({...form, department: e.target.value})}
                  required
                >
                  <option value="">-- Ch·ªçn ph√≤ng ban --</option>
                  {DEPARTMENTS.map(d => <option key={d} value={d}>{d}</option>)}
                </select>
              </div>
              <div className="col">
                <select
                  className="form-control"
                  value={form.position}
                  onChange={(e)=>setForm({...form, position: e.target.value})}
                  required
                >
                  <option value="">-- Ch·ªçn ch·ª©c v·ª• --</option>
                  {POSITIONS.map(p => <option key={p} value={p}>{p}</option>)}
                </select>
              </div>
            </div>

            <div className="row g-2 mt-2">
              <div className="col">
                <select
                  className="form-control"
                  value={form.role}
                  onChange={(e)=>setForm({...form, role: e.target.value})}
                >
                  {ROLES.map(r => <option key={r} value={r}>{r}</option>)}
                </select>
              </div>
              <div className="col">
                <button className="btn btn-primary w-100">
                  {editId ? "C·∫≠p nh·∫≠t" : "Th√™m nh√¢n vi√™n"}
                </button>
              </div>
              {editId && (
                <div className="col">
                  <button
                    type="button"
                    className="btn btn-secondary w-100"
                    onClick={() => {
                      setEditId(null);
                      setForm({ username: "", password: "", name: "", department: "", position: "", role: "employee" });
                    }}
                  >
                    H·ªßy
                  </button>
                </div>
              )}
            </div>
          </form>

          {/* Danh s√°ch nh√¢n vi√™n */}
          <h5>üìã Danh s√°ch nh√¢n vi√™n</h5>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Username</th>
                <th>Role</th>
                <th>H·ªç t√™n</th>
                <th>Ph√≤ng ban</th>
                <th>Ch·ª©c v·ª•</th>
                <th>H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {list.map(e => (
                <tr key={e._id}>
                  <td>{e.userId?.username}</td>
                  <td>{e.userId?.role}</td>
                  <td>{e.name}</td>
                  <td>{e.department}</td>
                  <td>{e.position}</td>
                  <td>
                    <button className="btn btn-sm btn-warning me-2" onClick={()=>onEdit(e)}>S·ª≠a</button>
                    <button className="btn btn-sm btn-danger" onClick={()=>onDelete(e._id)}>X√≥a</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

        </div>
      </div>
    </div>
  );
}

export default HRManagement;
