import React, { useEffect, useState } from "react";
import UserLayout from "../../layouts/UserLayout";
import { useAuth } from "../../context/AuthContext";
import api from "../../api";
import { useNavigate } from "react-router-dom";

export default function UserDashboard() {
  const { user } = useAuth();
  const navigate = useNavigate();

  const [attendance, setAttendance] = useState(0);
  const [notifications, setNotifications] = useState(0);
  const [performance, setPerformance] = useState(0);

  useEffect(() => {
    loadAttendance();
    loadNotifications();
    loadPerformance();
  }, []);

  const loadAttendance = async () => {
    try {
      const res = await api.get("/attendance");
      const totalDays = res.data?.reduce((sum, x) => sum + (x.totalDays || 0), 0);
      setAttendance(totalDays);
    } catch (err) {
      console.error("‚ùå Attendance load error:", err);
    }
  };

  const loadNotifications = async () => {
    try {
      const res = await api.get("/notifications");
      setNotifications(res.data?.length || 0);
    } catch (err) {
      console.error("‚ùå Notifications load error:", err);
    }
  };

  const loadPerformance = async () => {
    try {
      const res = await api.get("/employees/performance");
      if (res.data?.length > 0) {
        const scores = res.data.map((r) => {
          const values = [
            r.tasksCompleted,
            r.communication,
            r.technical,
            r.attitude,
          ].filter((v) => typeof v === "number");
          return values.reduce((a, b) => a + b, 0) / values.length;
        });
        const avg = scores.reduce((a, b) => a + b, 0) / scores.length;
        setPerformance(avg.toFixed(1));
      }
    } catch (err) {
      console.error("‚ùå Performance load error:", err);
    }
  };

  return (
    <UserLayout role={user?.role}>
      <h2 className="mb-3">üè† Trang t·ªïng quan</h2>

      {/* Banner */}
      <div className="alert alert-info shadow-sm">
        Xin ch√†o <b>{user?.username}</b> üëã <br />
        H√£y s·ª≠ d·ª•ng menu b√™n tr√°i ƒë·ªÉ truy c·∫≠p c√°c ch·ª©c nƒÉng:
        <ul className="mt-2 mb-0">
          <li>üìÑ H·ªì s∆° c√° nh√¢n</li>
          <li>üïí Ch·∫•m c√¥ng</li>
          <li>üìÇ T√†i li·ªáu</li>
          <li>üì¢ Th√¥ng b√°o</li>
          <li>üìä Hi·ªáu su·∫•t</li>
          <li>üí¨ Chat n·ªôi b·ªô</li>
          <li>üí∞ Xem l∆∞∆°ng</li>
          <li>üìå Xin ngh·ªâ ph√©p</li>
        </ul>
      </div>

      {/* CARD DASHBOARD */}
      <div className="row g-3 mt-3">

        {/* üïí Ch·∫•m c√¥ng */}
        <div className="col-md-4">
          <div
            className="card shadow-sm p-3 text-center dashboard-card"
            onClick={() => navigate("/user/attendance")}
            style={{ cursor: "pointer" }}
          >
            <h5>üïí Ch·∫•m c√¥ng</h5>
            <p className="text-muted small">Theo d√µi gi·ªù l√†m m·ªói ng√†y</p>
            <h3 className="text-primary">{attendance} ng√†y</h3>
          </div>
        </div>

        {/* üì¢ Th√¥ng b√°o */}
        <div className="col-md-4">
          <div
            className="card shadow-sm p-3 text-center dashboard-card"
            onClick={() => navigate("/user/notifications")}
            style={{ cursor: "pointer" }}
          >
            <h5>üì¢ Th√¥ng b√°o</h5>
            <p className="text-muted small">Xem th√¥ng b√°o c√¥ng ty</p>
            <h3 className="text-danger">{notifications}</h3>
          </div>
        </div>

        {/* üìä Hi·ªáu su·∫•t */}
        <div className="col-md-4">
          <div
            className="card shadow-sm p-3 text-center dashboard-card"
            onClick={() => navigate("/user/performance")}
            style={{ cursor: "pointer" }}
          >
            <h5>üìä Hi·ªáu su·∫•t</h5>
            <p className="text-muted small">K·∫øt qu·∫£ ƒë√°nh gi√° c·ªßa b·∫°n</p>
            <h3 className="text-success">{performance}</h3>
          </div>
        </div>

        {/* ‚≠ê‚≠ê MANAGER ONLY ‚Äî QU·∫¢N L√ù NH√ìM ‚≠ê‚≠ê */}
        {user?.role === "manager" && (
          <>

            <div className="col-md-6">
              <div
                className="card shadow-sm p-3 text-center dashboard-card"
                onClick={() => navigate("/user/manage-group")}
                style={{ cursor: "pointer" }}
              >
                <h5>üë• Qu·∫£n l√Ω nh√≥m</h5>
                <p className="text-muted small">Th√™m & qu·∫£n l√Ω nh√¢n vi√™n d∆∞·ªõi quy·ªÅn</p>
              </div>
            </div>

            <div className="col-md-6">
              <div
                className="card shadow-sm p-3 text-center dashboard-card"
                onClick={() => navigate("/user/manager-attendance")}
                style={{ cursor: "pointer" }}
              >
                <h5>üïí Ch·∫•m c√¥ng nh√¢n vi√™n</h5>
                <p className="text-muted small">Ch·∫•m c√¥ng nh√≥m b·∫°n qu·∫£n l√Ω</p>
              </div>
            </div>

          </>
        )}

      </div>

      {/* CSS hover nh·ªè */}
      <style>
        {`
          .dashboard-card {
            transition: 0.25s;
            border-radius: 10px;
          }
          .dashboard-card:hover {
            transform: translateY(-4px);
            box-shadow: 0 6px 18px rgba(0,0,0,0.15);
          }
        `}
      </style>
    </UserLayout>
  );
}
