import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function SalaryManagement({ role }) {
  const [list, setList] = useState([]);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({ amount: "", date: "" });

  const load = async () => {
    try {
      if (role === "admin") {
        const res = await api.get("/admin/salary");
        setList(res.data);
      } else {
        const res = await api.get("/salary/me");
        setList(res.data);
      }
    } catch (err) {
      alert("❌ Không tải được dữ liệu lương");
    }
  };

  useEffect(() => { load(); }, [role]);

  const startEdit = (s) => {
    setEditId(s._id);
    setForm({ amount: s.amount, date: s.date?.substring(0,10) });
  };

  const saveEdit = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/admin/salary/${editId}`, form);
      setEditId(null);
      setForm({ amount: "", date: "" });
      load();
    } catch (err) {
      alert("❌ Lỗi cập nhật lương");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role={role === "admin" ? "admin" : "user"} />
        </div>
        <div className="col-9">
          <h3>💰 Quản lý lương ({role})</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                {role === "admin" ? <th>User</th> : null}
                <th>Số tiền</th>
                <th>Ngày nhận</th>
                {role === "admin" && <th>Hành động</th>}
              </tr>
            </thead>
            <tbody>
              {list.map(s => (
                <tr key={s._id}>
                  {role === "admin" ? <td>{s.userId?.username}</td> : null}
                  <td>
                    {editId === s._id ? (
                      <input
                        className="form-control"
                        value={form.amount}
                        onChange={e => setForm({ ...form, amount: e.target.value })}
                      />
                    ) : (
                      s.amount
                    )}
                  </td>
                  <td>
                    {editId === s._id ? (
                      <input
                        className="form-control"
                        type="date"
                        value={form.date}
                        onChange={e => setForm({ ...form, date: e.target.value })}
                      />
                    ) : (
                      new Date(s.date).toLocaleDateString()
                    )}
                  </td>
                  {role === "admin" && (
                    <td>
                      {editId === s._id ? (
                        <button className="btn btn-success btn-sm" onClick={saveEdit}>Lưu</button>
                      ) : (
                        <button className="btn btn-warning btn-sm" onClick={() => startEdit(s)}>Sửa</button>
                      )}
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
export default SalaryManagement;
