import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function PerformanceReviewAdmin() {
  const [reviews, setReviews] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({
    userId: "",
    period: "",
    completedTasks: 0,
    communication: 0,
    technical: 0,
    attitude: 0,
    quality: "Trung bình",
  });

  // Load dữ liệu
  const load = async () => {
    try {
      const res = await api.get("/admin/performance");
      setReviews(res.data);
      const emp = await api.get("/admin/employees");
      setEmployees(emp.data);
    } catch (err) {
      console.error("❌ API Error:", err);
      alert("Không tải được dữ liệu");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // Submit thêm/sửa
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await api.put(`/admin/performance/${editId}`, form);
        alert("✅ Cập nhật thành công");
      } else {
        await api.post("/admin/performance", form);
        alert("✅ Thêm đánh giá thành công");
      }
      setForm({
        userId: "",
        period: "",
        completedTasks: 0,
        communication: 0,
        technical: 0,
        attitude: 0,
        quality: "Trung bình",
      });
      setEditId(null);
      load();
    } catch (err) {
      console.error("❌ Lỗi thêm/cập nhật:", err);
      alert("Không thể lưu đánh giá");
    }
  };

  // Xóa
  const handleDelete = async (id) => {
    if (!window.confirm("Bạn có chắc muốn xóa đánh giá này?")) return;
    try {
      await api.delete(`/admin/performance/${id}`);
      alert("🗑️ Đã xóa đánh giá");
      load();
    } catch (err) {
      console.error("❌ Lỗi xóa:", err);
      alert("Không thể xóa");
    }
  };

  // Bắt đầu sửa
  const startEdit = (r) => {
    setEditId(r._id);
    setForm({
      userId: r.userId?._id || "",
      period: r.period || "",
      completedTasks: r.completedTasks || 0,
      communication: r.communication || 0,
      technical: r.technical || 0,
      attitude: r.attitude || 0,
      quality: r.quality || "Trung bình",
    });
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="admin" />
        </div>
        <div className="col-9">
          <h3>📊 Đánh giá hiệu suất</h3>

          {/* Form */}
          <form className="card p-3 mb-3" onSubmit={handleSubmit}>
            <select
              className="form-control mb-2"
              value={form.userId}
              onChange={(e) => setForm({ ...form, userId: e.target.value })}
              required
            >
              <option value="">-- Chọn nhân viên --</option>
              {employees.map((emp) => (
                <option key={emp._id} value={emp.userId?._id}>
                  {emp.name} ({emp.department})
                </option>
              ))}
            </select>

            <input
              type="text"
              className="form-control mb-2"
              placeholder="Kỳ đánh giá (ví dụ: Tháng 1/2025)"
              value={form.period}
              onChange={(e) => setForm({ ...form, period: e.target.value })}
              required
            />

            <input
              type="number"
              className="form-control mb-2"
              placeholder="Số nhiệm vụ hoàn thành"
              value={form.completedTasks}
              onChange={(e) =>
                setForm({ ...form, completedTasks: Number(e.target.value) })
              }
            />

            {/* Communication */}
            <label>Giao tiếp</label>
            <select
              className="form-control mb-2"
              value={form.communication}
              onChange={(e) =>
                setForm({ ...form, communication: Number(e.target.value) })
              }
            >
              <option value={0}>Không biết (0)</option>
              <option value={2}>Kém (2)</option>
              <option value={4}>Có thể giao tiếp (4)</option>
              <option value={6}>Khá (6)</option>
              <option value={8}>Tốt (8)</option>
              <option value={10}>Giỏi (10)</option>
            </select>

            {/* Technical */}
            <label>Kỹ thuật</label>
            <select
              className="form-control mb-2"
              value={form.technical}
              onChange={(e) =>
                setForm({ ...form, technical: Number(e.target.value) })
              }
            >
              <option value={0}>Không biết (0)</option>
              <option value={2}>Kém (2)</option>
              <option value={4}>Cơ bản (4)</option>
              <option value={6}>Khá (6)</option>
              <option value={8}>Tốt (8)</option>
              <option value={10}>Giỏi (10)</option>
            </select>

            {/* Attitude */}
            <label>Thái độ</label>
            <select
              className="form-control mb-2"
              value={form.attitude}
              onChange={(e) =>
                setForm({ ...form, attitude: Number(e.target.value) })
              }
            >
              <option value={0}>Tiêu cực (0)</option>
              <option value={10}>Tích cực (10)</option>
            </select>

            {/* Quality */}
            <label>Chất lượng tổng thể</label>
            <select
              className="form-control mb-2"
              value={form.quality}
              onChange={(e) => setForm({ ...form, quality: e.target.value })}
            >
              <option value="Tốt">Tốt</option>
              <option value="Trung bình">Trung bình</option>
              <option value="Kém">Kém</option>
            </select>

            <button className="btn btn-primary">
              {editId ? "💾 Lưu thay đổi" : "➕ Thêm đánh giá"}
            </button>
            {editId && (
              <button
                type="button"
                className="btn btn-secondary ms-2"
                onClick={() => {
                  setEditId(null);
                  setForm({
                    userId: "",
                    period: "",
                    completedTasks: 0,
                    communication: 0,
                    technical: 0,
                    attitude: 0,
                    quality: "Trung bình",
                  });
                }}
              >
                Hủy
              </button>
            )}
          </form>

          {/* Bảng */}
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Nhân viên</th>
                <th>Kỳ</th>
                <th>Nhiệm vụ</th>
                <th>Giao tiếp</th>
                <th>Kỹ thuật</th>
                <th>Thái độ</th>
                <th>Chất lượng</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {reviews.map((r) => (
                <tr key={r._id}>
                  <td>{r.userId?.username}</td>
                  <td>{r.period}</td>
                  <td>{r.completedTasks}</td>
                  <td>{r.communication}</td>
                  <td>{r.technical}</td>
                  <td>{r.attitude === 10 ? "Tích cực" : "Tiêu cực"}</td>
                  <td>{r.quality}</td>
                  <td>
                    <button
                      className="btn btn-warning btn-sm me-2"
                      onClick={() => startEdit(r)}
                    >
                      Sửa
                    </button>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => handleDelete(r._id)}
                    >
                      Xóa
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Tổng kết */}
          <div className="mt-3">
            <h5>📌 Thống kê</h5>
            <p>
              Tổng số nhiệm vụ đã hoàn thành:{" "}
              <b>
                {reviews.reduce(
                  (sum, r) => sum + (r.completedTasks || 0),
                  0
                )}
              </b>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PerformanceReviewAdmin;
