import React, { useEffect, useState } from "react";
import api from "../../api";
import AdminLayout from "../../layouts/AdminLayout";

function WorkScheduleAdmin() {
  const [list, setList] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [editId, setEditId] = useState(null);

  const [form, setForm] = useState({
    task: "",
    department: "",
    assignedTo: "",
    startDate: "",
    endDate: "",
  });

  // Load d·ªØ li·ªáu
  const load = async () => {
    try {
      const res = await api.get("/admin/work-schedule");
      setList(res.data);

      const empRes = await api.get("/admin/employees");
      setEmployees(empRes.data);
    } catch (err) {
      console.error("‚ùå API Error:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // Khi ch·ªçn nh√¢n vi√™n t·ª± set ph√≤ng ban
  const handleEmployeeChange = (e) => {
    const userId = e.target.value;
    const emp = employees.find((emp) => emp.userId?._id === userId);
    setForm({
      ...form,
      assignedTo: userId,
      department: emp?.department || "",
    });
  };

  // Th√™m / s·ª≠a
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await api.put(`/admin/work-schedule/${editId}`, form);
        alert("‚úÖ C·∫≠p nh·∫≠t l·ªãch th√†nh c√¥ng");
      } else {
        await api.post("/admin/work-schedule", form);
        alert("‚úÖ Th√™m l·ªãch th√†nh c√¥ng");
      }

      setForm({
        task: "",
        department: "",
        assignedTo: "",
        startDate: "",
        endDate: "",
      });
      setEditId(null);
      load();
    } catch (err) {
      console.error("‚ùå L·ªói:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng th·ªÉ l∆∞u l·ªãch");
    }
  };

  // X√≥a
  const handleDelete = async (id) => {
    if (!window.confirm("B·∫°n ch·∫Øc ch·∫Øn mu·ªën x√≥a l·ªãch n√†y?")) return;

    try {
      await api.delete(`/admin/work-schedule/${id}`);
      alert("üóëÔ∏è ƒê√£ x√≥a l·ªãch");
      load();
    } catch (err) {
      console.error("‚ùå L·ªói:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng th·ªÉ x√≥a");
    }
  };

  // S·ª≠a
  const startEdit = (w) => {
    setEditId(w._id);
    setForm({
      task: w.task,
      department: w.department,
      assignedTo: w.assignedTo?._id,
      startDate: w.startDate?.split("T")[0],
      endDate: w.endDate?.split("T")[0],
    });
  };

  return (
    <AdminLayout>
      <h2>üìÖ Qu·∫£n l√Ω l·ªãch l√†m vi·ªác</h2>

      {/* Form */}
      <div className="card p-3 mb-3 mt-3">
        <h5>{editId ? "‚úèÔ∏è Ch·ªânh s·ª≠a l·ªãch" : "‚ûï Th√™m l·ªãch m·ªõi"}</h5>

        <form onSubmit={handleSubmit} className="mt-2">
          <div className="mb-2">
            <label className="form-label">Nhi·ªám v·ª•</label>
            <input
              type="text"
              className="form-control"
              value={form.task}
              onChange={(e) => setForm({ ...form, task: e.target.value })}
              required
            />
          </div>

          {/* Nh√¢n vi√™n */}
          <div className="mb-2">
            <label className="form-label">Nh√¢n vi√™n</label>
            <select
              className="form-control"
              value={form.assignedTo}
              onChange={handleEmployeeChange}
              required
            >
              <option value="">-- Ch·ªçn nh√¢n vi√™n --</option>
              {employees.map((emp) => (
                <option key={emp._id} value={emp.userId?._id}>
                  {emp.name} ({emp.userId?.username})
                </option>
              ))}
            </select>
          </div>

          {/* Ph√≤ng ban */}
          <div className="mb-2">
            <label className="form-label">Ph√≤ng ban</label>
            <input
              type="text"
              className="form-control"
              value={form.department}
              readOnly
            />
          </div>

          <div className="row">
            <div className="col">
              <label className="form-label">Ng√†y b·∫Øt ƒë·∫ßu</label>
              <input
                type="date"
                className="form-control"
                value={form.startDate}
                onChange={(e) => setForm({ ...form, startDate: e.target.value })}
                required
              />
            </div>

            <div className="col">
              <label className="form-label">Ng√†y k·∫øt th√∫c</label>
              <input
                type="date"
                className="form-control"
                value={form.endDate}
                onChange={(e) => setForm({ ...form, endDate: e.target.value })}
                required
              />
            </div>
          </div>

          <button className="btn btn-primary mt-3">
            {editId ? "üíæ L∆∞u thay ƒë·ªïi" : "‚ûï Th√™m l·ªãch"}
          </button>
          {editId && (
            <button
              type="button"
              className="btn btn-secondary mt-3 ms-2"
              onClick={() => {
                setEditId(null);
                setForm({
                  task: "",
                  department: "",
                  assignedTo: "",
                  startDate: "",
                  endDate: "",
                });
              }}
            >
              H·ªßy
            </button>
          )}
        </form>
      </div>

      {/* Danh s√°ch */}
      <div className="card p-3">
        <h5>üìã Danh s√°ch l·ªãch l√†m vi·ªác</h5>

        <table className="table table-bordered mt-2">
          <thead>
            <tr>
              <th>Nhi·ªám v·ª•</th>
              <th>Ph√≤ng ban</th>
              <th>Nh√¢n vi√™n</th>
              <th>B·∫Øt ƒë·∫ßu</th>
              <th>K·∫øt th√∫c</th>
              <th>H√†nh ƒë·ªông</th>
            </tr>
          </thead>

          <tbody>
            {list.map((w) => (
              <tr key={w._id}>
                <td>{w.task}</td>
                <td>{w.department}</td>
                <td>{w.assignedTo?.username}</td>
                <td>{new Date(w.startDate).toLocaleDateString("vi-VN")}</td>
                <td>{new Date(w.endDate).toLocaleDateString("vi-VN")}</td>
                <td>
                  <button
                    className="btn btn-warning btn-sm me-2"
                    onClick={() => startEdit(w)}
                  >
                    S·ª≠a
                  </button>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDelete(w._id)}
                  >
                    X√≥a
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </AdminLayout>
  );
