import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function SalaryManagement({ role }) {
  const [list, setList] = useState([]);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({ amount: "", month: "" });

  const load = async () => {
    try {
      if (role === "admin") {
        const res = await api.get("/admin/salary");
        setList(res.data);
      } else {
        const res = await api.get("/salary/me");
        setList(res.data);
      }
    } catch (err) {
      console.error(err);
      alert("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu l∆∞∆°ng");
    }
  };

  useEffect(() => { load(); }, [role]);

  const startEdit = (s) => {
    setEditId(s._id);
    setForm({ amount: s.amount, month: s.month || "" });
  };

  const saveEdit = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/admin/salary/${editId}`, form);
      setEditId(null);
      setForm({ amount: "", month: "" });
      load();
    } catch (err) {
      alert("‚ùå L·ªói c·∫≠p nh·∫≠t l∆∞∆°ng");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role={role === "admin" ? "admin" : "user"} />
        </div>
        <div className="col-9">
          <h3>üí∞ Qu·∫£n l√Ω l∆∞∆°ng ({role})</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                {role === "admin" && <th>User</th>}
                <th>S·ªë ti·ªÅn</th>
                <th>Th√°ng</th>
                {role === "admin" && <th>H√†nh ƒë·ªông</th>}
              </tr>
            </thead>
            <tbody>
              {list.map((s) => (
                <tr key={s._id}>
                  {role === "admin" && <td>{s.userId?.username}</td>}
                  <td>
                    {editId === s._id ? (
                      <input
                        className="form-control"
                        value={form.amount}
                        onChange={(e) =>
                          setForm({ ...form, amount: e.target.value })
                        }
                      />
                    ) : (
                      s.amount
                    )}
                  </td>
                  <td>
                    {editId === s._id ? (
                      <input
                        className="form-control"
                        type="month"
                        value={form.month}
                        onChange={(e) =>
                          setForm({ ...form, month: e.target.value })
                        }
                      />
                    ) : (
                      s.month
                    )}
                  </td>
                  {role === "admin" && (
                    <td>
                      {editId === s._id ? (
                        <button
                          className="btn btn-success btn-sm"
                          onClick={saveEdit}
                        >
                          L∆∞u
                        </button>
                      ) : (
                        <button
                          className="btn btn-warning btn-sm"
                          onClick={() => startEdit(s)}
                        >
                          S·ª≠a
                        </button>
                      )}
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default SalaryManagement;
