import React, { useEffect, useState, useContext } from "react";
import io from "socket.io-client";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";
import { AuthContext } from "../../context/AuthContext";

const socket = io("http://localhost:5000");

function Chat() {
  const { user } = useContext(AuthContext);
  const [employees, setEmployees] = useState([]);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [target, setTarget] = useState(null); // userId ho·∫∑c department
  const [isGroup, setIsGroup] = useState(false);

  // join socket room
  useEffect(() => {
    if (user?._id) {
      socket.emit("join", { userId: user._id, department: user.department });
    }

    socket.on("receive_message", (payload) => {
      if (
        (payload.toUserId === user._id && !isGroup) ||
        (payload.department === user.department && isGroup)
      ) {
        setMessages((prev) => [...prev, payload]);
      }
    });

    return () => socket.off("receive_message");
  }, [user, isGroup]);

  // load employees
  const loadEmployees = async () => {
    try {
      const res = await api.get("/admin/employees");
      setEmployees(res.data);
    } catch (err) {
      console.error("‚ùå L·ªói loadEmployees:", err);
    }
  };

  // load messages
  const loadMessages = async () => {
    if (!target) return;
    const type = isGroup ? "group" : "private";
    try {
      const res = await api.get(`/messages/${type}/${target}`);
      setMessages(res.data);
    } catch (err) {
      console.error("‚ùå L·ªói loadMessages:", err);
    }
  };

  useEffect(() => {
    loadEmployees();
  }, []);

  useEffect(() => {
    loadMessages();
  }, [target]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;

    const payload = {
      message: text,
      type: isGroup ? "group" : "private",
      toUserId: isGroup ? null : target,
      department: isGroup ? user.department : null,
    };

    socket.emit("send_message", {
      ...payload,
      fromUserId: user._id,
    });

    await api.post("/messages", payload);
    setText("");
    setMessages([...messages, { ...payload, fromUserId: { username: user.username } }]);
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="employee" />
        </div>
        <div className="col-9">
          <h3>üí¨ Tr√≤ chuy·ªán</h3>
          <div className="d-flex mb-2">
            <button
              className={`btn me-2 ${isGroup ? "btn-secondary" : "btn-outline-secondary"}`}
              onClick={() => setIsGroup(false)}
            >
              Ri√™ng t∆∞
            </button>
            <button
              className={`btn ${isGroup ? "btn-outline-secondary" : "btn-secondary"}`}
              onClick={() => setIsGroup(true)}
            >
              Nh√≥m ph√≤ng ban
            </button>
          </div>

          {!isGroup ? (
            <select
              className="form-control mb-3"
              onChange={(e) => setTarget(e.target.value)}
              value={target || ""}
            >
              <option value="">-- Ch·ªçn ng∆∞·ªùi ƒë·ªÉ chat --</option>
              {employees.map((emp) => (
                <option key={emp._id} value={emp.userId?._id}>
                  {emp.name} ({emp.userId?.username})
                </option>
              ))}
            </select>
          ) : (
            <div className="alert alert-info">
              üí¨ B·∫°n ƒëang tr√≤ chuy·ªán trong ph√≤ng ban: <strong>{user.department}</strong>
            </div>
          )}

          <div
            style={{
              border: "1px solid #ccc",
              height: 350,
              overflowY: "auto",
              padding: 10,
              marginBottom: 10,
            }}
          >
            {messages.map((msg, idx) => (
              <div
                key={idx}
                style={{
                  textAlign: msg.fromUserId?.username === user.username ? "right" : "left",
                }}
              >
                <strong>{msg.fromUserId?.username || "B·∫°n"}:</strong> {msg.message}
              </div>
            ))}
          </div>

          <form onSubmit={sendMessage} className="d-flex">
            <input
              className="form-control me-2"
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Nh·∫≠p tin nh·∫Øn..."
            />
            <button className="btn btn-primary">G·ª≠i</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Chat;
