// src/pages/user/DocumentManagementUser.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import UserLayout from "../../layouts/UserLayout";
import { useAuth } from "../../context/AuthContext";

export default function DocumentManagementUser() {
  const { user } = useAuth();   // <-- d√πng context user
  const [docs, setDocs] = useState([]);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [loading, setLoading] = useState(false);

  const currentUserId = user?.id;
  const currentRole = user?.role;

  const [currentUserDept, setCurrentUserDept] = useState(null);

  // L·∫•y ph√≤ng ban t·ª´ backend
  useEffect(() => {
    const loadDept = async () => {
      try {
        const res = await api.get("/employees/me");
        setCurrentUserDept(res.data.department);
      } catch (err) {
        console.error("‚ùå L·ªói load department:", err);
      }
    };
    loadDept();
  }, []);

  const load = async () => {
    try {
      const res = await api.get("/employees/documents");
      setDocs(res.data || []);
    } catch (err) {
      console.error("‚ùå Error load documents:", err);
    }
  };

  useEffect(() => {
    load();
  }, []);

  const downloadFile = async (id) => {
    try {
      const response = await api.get(`/employees/documents/download/${id}`, {
        responseType: "blob",
      });

      const disposition = response.headers["content-disposition"];
      let filename = "tai-lieu";

      if (disposition) {
        const utf8 = disposition.match(/filename\*=UTF-8''(.+)/);
        const normal = disposition.match(/filename="(.+)"/);
        if (utf8) filename = decodeURIComponent(utf8[1]);
        else if (normal) filename = decodeURIComponent(normal[1]);
      }

      const blob = new Blob([response.data]);
      const link = document.createElement("a");
      link.href = window.URL.createObjectURL(blob);
      link.setAttribute("download", filename);
      link.click();
    } catch (err) {
      console.error("‚ùå Error download:", err);
    }
  };

  const uploadFile = async (e) => {
    e.preventDefault();
    if (!file || !title.trim()) return alert("Thi·∫øu th√¥ng tin!");

    const formData = new FormData();
    formData.append("title", title);
    formData.append("file", file);

    try {
      setLoading(true);
      await api.post("/employees/documents", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setTitle("");
      setFile(null);
      load();
    } catch (err) {
      console.error("‚ùå Error upload:", err);
    } finally {
      setLoading(false);
    }
  };

  const deleteFile = async (id) => {
    if (!window.confirm("X√≥a t√†i li·ªáu n√†y?")) return;
    try {
      await api.delete(`/employees/documents/${id}`);
      load();
    } catch (err) {
      console.error("‚ùå Error delete:", err);
    }
  };

  const visibleDocs = docs.filter(
    (d) =>
      d.department === "general" ||
      d.department === currentUserDept
  );

  return (
    <UserLayout>
      <h2 className="mb-3">üìÇ T√†i li·ªáu c·ªßa t√¥i</h2>

      <div className="card p-3 mb-4">
        <h5 className="mb-3">‚¨ÜÔ∏è Upload t√†i li·ªáu</h5>

        <form onSubmit={uploadFile}>
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Ti√™u ƒë·ªÅ t√†i li·ªáu..."
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />

          <input
            type="file"
            className="form-control mb-2"
            onChange={(e) => setFile(e.target.files[0])}
          />

          <button className="btn btn-primary" disabled={loading}>
            {loading ? "ƒêang t·∫£i..." : "T·∫£i l√™n"}
          </button>
        </form>
      </div>

      <div className="card p-3">
        <h5>üìë Danh s√°ch t√†i li·ªáu</h5>

        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Ph√≤ng ban</th>
              <th>Th∆∞ m·ª•c</th>
              <th>Ti√™u ƒë·ªÅ</th>
              <th>Ng∆∞·ªùi t·∫£i l√™n</th>
              <th className="text-center">H√†nh ƒë·ªông</th>
            </tr>
          </thead>
          <tbody>
            {visibleDocs.map((d) => {
              const isOwner = String(d.uploadedBy?._id) === String(currentUserId);
              const canDelete = isOwner || currentRole === "admin";

              return (
                <tr key={d._id}>
                  <td>{d.department}</td>
                  <td>{d.folder || "‚Äî"}</td>
                  <td>{d.title}</td>
                  <td>{d.uploadedBy?.username}</td>
                  <td className="text-center">
                    <button
                      className="btn btn-success btn-sm me-2"
                      onClick={() => downloadFile(d._id)}
                    >
                      ‚¨áÔ∏è
                    </button>

                    {canDelete && (
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => deleteFile(d._id)}
                      >
                        üóëÔ∏è
                      </button>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </UserLayout>
  );
}
