// src/pages/user/LeaveRequest.js
import React, { useState } from "react";
import api from "../../api";
import UserLayout from "../../layouts/UserLayout";
import { useAuth } from "../../context/AuthContext";

export default function LeaveRequest() {
  const { user } = useAuth();   // üî• L·∫•y user th·∫≠t t·ª´ context
  const [form, setForm] = useState({ from: "", to: "", reason: "" });
  const [loading, setLoading] = useState(false);

  // Tr√°nh l·ªói khi user ch∆∞a load ‚Üí ngƒÉn vƒÉng login
  if (!user) {
    return (
      <UserLayout>
        <div className="p-3 text-center">ƒêang t·∫£i d·ªØ li·ªáu...</div>
      </UserLayout>
    );
  }

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!form.from || !form.to) return alert("Vui l√≤ng ch·ªçn ƒë·∫ßy ƒë·ªß ng√†y!");

    if (new Date(form.from) > new Date(form.to))
      return alert("‚ùå Ng√†y b·∫Øt ƒë·∫ßu kh√¥ng ƒë∆∞·ª£c l·ªõn h∆°n ng√†y k·∫øt th√∫c!");

    try {
      setLoading(true);
      await api.post("/leave-requests", form);
      alert("‚úÖ ƒê√£ g·ª≠i ƒë∆°n ngh·ªâ ph√©p");

      setForm({ from: "", to: "", reason: "" });
    } catch (err) {
      console.error("‚ùå L·ªói g·ª≠i ƒë∆°n:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ g·ª≠i ƒë∆°n ngh·ªâ ph√©p!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <UserLayout role={user.role}>   {/* üî• B·∫ÆT BU·ªòC: role ph·∫£i t·ª´ context */}
      <h2 className="mb-3">üìå Xin ngh·ªâ ph√©p</h2>

      <div className="card p-3">
        <form onSubmit={onSubmit}>
          <div className="row g-3">
            <div className="col-md-6">
              <label className="form-label">T·ª´ ng√†y</label>
              <input
                type="date"
                className="form-control"
                value={form.from}
                onChange={(e) => setForm({ ...form, from: e.target.value })}
                required
              />
            </div>

            <div className="col-md-6">
              <label className="form-label">ƒê·∫øn ng√†y</label>
              <input
                type="date"
                className="form-control"
                value={form.to}
                onChange={(e) => setForm({ ...form, to: e.target.value })}
                required
              />
            </div>
          </div>

          <label className="form-label mt-3">L√Ω do ngh·ªâ ph√©p</label>
          <textarea
            className="form-control"
            rows={3}
            placeholder="Nh·∫≠p l√Ω do..."
            value={form.reason}
            onChange={(e) => setForm({ ...form, reason: e.target.value })}
            required
          />

          <button className="btn btn-primary mt-3" disabled={loading}>
            {loading ? "ƒêang g·ª≠i..." : "üì§ G·ª≠i ƒë∆°n"}
          </button>
        </form>
      </div>
    </UserLayout>
  );
}
