import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { AuthProvider } from "./context/AuthContext";
import Protected from "./components/Protected";
import Register from "./pages/Register";
import Login from "./pages/Login";
import Dashboard from "./pages/Dashboard";
import Employees from "./pages/Employees";
import Attendance from "./pages/Attendance";
import LeaveRequests from "./pages/LeaveRequests";
import Messages from "./pages/Messages";

<Routes>
  <Route path="/" element={<Login />} />
  <Route path="/register" element={<Register />} />

</Routes>

function App() {
  return (
    <BrowserRouter>
      <AuthProvider>
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/dashboard" element={<Protected><Dashboard /></Protected>} />
          <Route path="/employees" element={<Protected roles={["admin","manager"]}><Employees /></Protected>} />
          <Route path="/attendance" element={<Protected roles={["employee","admin","manager"]}><Attendance /></Protected>} />
          <Route path="/leave-requests" element={<Protected roles={["employee","admin","manager"]}><LeaveRequests /></Protected>} />
          <Route path="/messages" element={<Protected roles={["employee","admin","manager"]}><Messages /></Protected>} />
        </Routes>
      </AuthProvider>
    </BrowserRouter>
  );
}

export default App;
