import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function SalaryManagement({ role }) {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({ userId: "", amount: "", month: "" });

  const load = async () => {
    try {
      if (role === "admin") {
        // L·∫•y danh s√°ch t·∫•t c·∫£ l∆∞∆°ng
        const res = await api.get("/admin/salary");
        setList(res.data);
      } else {
        // User ch·ªâ l·∫•y l∆∞∆°ng c·ªßa m√¨nh
        const res = await api.get("/salary/me");
        setList(res.data);
      }
    } catch (err) {
      console.error(err);
      alert("Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu l∆∞∆°ng");
    }
  };

  useEffect(() => { load(); }, [role]);

  const add = async (e) => {
    e.preventDefault();
    if (role !== "admin") return;
    try {
      await api.post("/admin/salary", form);
      setForm({ userId: "", amount: "", month: "" });
      load();
    } catch (err) {
      alert(err.response?.data?.error || "L·ªói th√™m l∆∞∆°ng");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role={role === "admin" ? "admin" : "user"} />
        </div>
        <div className="col-9">
          <h3>üí∞ Qu·∫£n l√Ω l∆∞∆°ng ({role})</h3>

          {role === "admin" && (
            <form onSubmit={add} className="mb-3">
              <div className="row g-2">
                <div className="col">
                  <input
                    className="form-control"
                    placeholder="UserID"
                    value={form.userId}
                    onChange={e=>setForm({...form,userId:e.target.value})}
                  />
                </div>
                <div className="col">
                  <input
                    className="form-control"
                    placeholder="S·ªë ti·ªÅn"
                    value={form.amount}
                    onChange={e=>setForm({...form,amount:e.target.value})}
                  />
                </div>
                <div className="col">
                  <input
                    className="form-control"
                    placeholder="Th√°ng (YYYY-MM)"
                    value={form.month}
                    onChange={e=>setForm({...form,month:e.target.value})}
                  />
                </div>
                <div className="col">
                  <button className="btn btn-primary w-100">Th√™m</button>
                </div>
              </div>
            </form>
          )}

          <table className="table table-bordered">
            <thead>
              <tr>
                {role === "admin" ? <th>User</th> : null}
                <th>S·ªë ti·ªÅn</th>
                <th>Th√°ng</th>
              </tr>
            </thead>
            <tbody>
              {list.map(s => (
                <tr key={s._id}>
                  {role === "admin" ? <td>{s.userId?.username}</td> : null}
                  <td>{s.amount}</td>
                  <td>{s.month}</td>
                </tr>
              ))}
            </tbody>
          </table>

        </div>
      </div>
    </div>
  );
}
export default SalaryManagement;
