import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function SalaryManagement({ role }) {
  const [list, setList] = useState([]);
  const [error, setError] = useState(null);

  const load = async () => {
    try {
      let res;
      if (role === "admin") {
        res = await api.get("/admin/salary");   // Admin g·ªçi API qu·∫£n l√Ω to√†n b·ªô
      } else {
        res = await api.get("/salary/me");      // User g·ªçi API c√° nh√¢n
      }
      setList(res.data);
    } catch (err) {
      console.error("‚ùå API Error:", err.response?.data || err.message);
      setError("Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu l∆∞∆°ng");
    }
  };

  useEffect(() => { load(); }, [role]);

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role={role === "admin" ? "admin" : "user"} />
        </div>
        <div className="col-9">
          <h3>üí∞ Qu·∫£n l√Ω l∆∞∆°ng ({role})</h3>
          {error && <div className="alert alert-danger">{error}</div>}
          <table className="table table-bordered">
            <thead>
              <tr>
                {role === "admin" && <th>Username</th>}
                <th>S·ªë ti·ªÅn</th>
                <th>Ng√†y nh·∫≠n</th>
              </tr>
            </thead>
            <tbody>
              {list.map((s) => (
                <tr key={s._id}>
                  {role === "admin" && <td>{s.userId?.username}</td>}
                  <td>{s.amount}</td>
                  <td>{new Date(s.date).toLocaleDateString("vi-VN")}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default SalaryManagement;
