// src/pages/user/SalaryManagement.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import UserLayout from "../../layouts/UserLayout";
import { useAuth } from "../../context/AuthContext";

const fmtMoney = (v) =>
  typeof v === "number"
    ? v.toLocaleString("vi-VN")
    : (Number(v || 0)).toLocaleString("vi-VN");

const ymNow = () => {
  const d = new Date();
  return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, "0")}`;
};

export default function SalaryManagement() {
  const { user } = useAuth(); // üî• l·∫•y role th·∫≠t
  const [month, setMonth] = useState(ymNow());
  const [salary, setSalary] = useState(null);
  const [loading, setLoading] = useState(false);

  const load = async () => {
    try {
      setLoading(true);
      const res = await api.get(`/salary/me?month=${encodeURIComponent(month)}`);
      setSalary(res.data?.[0] || null);
    } catch (err) {
      console.error("‚ùå Salary load error:", err.response?.data || err.message);
      alert("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c d·ªØ li·ªáu l∆∞∆°ng");
    } finally {
      setLoading(false);
    }
  };

  // ‚õî Ch·ªâ load khi user ƒë√£ c√≥ ‚Üí tr√°nh l·ªói vƒÉng
  useEffect(() => {
    if (user) load();
  }, [user, month]);

  if (!user) {
    return (
      <UserLayout>
        <div className="text-center mt-4">ƒêang t·∫£i ng∆∞·ªùi d√πng...</div>
      </UserLayout>
    );
  }

  if (loading)
    return (
      <UserLayout role={user.role}>
        <div className="text-center mt-4">
          <div className="spinner-border text-primary"></div>
          <div className="mt-2">ƒêang t·∫£i d·ªØ li·ªáu...</div>
        </div>
      </UserLayout>
    );

  if (!salary)
    return (
      <UserLayout role={user.role}>
        <h2>üí∞ L∆∞∆°ng c·ªßa t√¥i</h2>
        <div className="alert alert-info mt-3">
          Kh√¥ng c√≥ d·ªØ li·ªáu l∆∞∆°ng trong th√°ng n√†y.
        </div>
      </UserLayout>
    );

  return (
    <UserLayout role={user.role}>   {/* üî• Fix c·ª©ng role=employee */}
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2 className="mb-0">üí∞ L∆∞∆°ng c·ªßa t√¥i</h2>

        <div className="d-flex gap-2">
          <input
            type="month"
            className="form-control"
            style={{ width: 180 }}
            value={month}
            onChange={(e) => setMonth(e.target.value)}
          />
          <button className="btn btn-outline-secondary" onClick={load}>
            ‚ü≥ L√†m m·ªõi
          </button>
        </div>
      </div>

      {/* Cards t·ªïng quan */}
      <div className="row g-3 mb-3">
        <div className="col-md-2 col-sm-4">
          <div className="card p-2 text-center shadow-sm">
            <div className="text-muted small">Th√°ng</div>
            <div className="fw-bold">{salary.month}</div>
          </div>
        </div>

        <div className="col-md-2 col-sm-4">
          <div className="card p-2 text-center shadow-sm">
            <div className="text-muted small">Ng√†y c√¥ng</div>
            <div className="fw-bold">{salary.totalDays}</div>
          </div>
        </div>

        <div className="col-md-2 col-sm-4">
          <div className="card p-2 text-center shadow-sm">
            <div className="text-muted small">Ph√∫t ƒëi tr·ªÖ</div>
            <div className="fw-bold">{salary.totalLateMinutes}</div>
          </div>
        </div>

        <div className="col-md-2 col-sm-4">
          <div className="card p-2 text-center shadow-sm">
            <div className="text-muted small">Ti·ªÅn ph·∫°t</div>
            <div className="fw-bold text-danger">
              {fmtMoney(salary.penalty)} ƒë
            </div>
          </div>
        </div>

        <div className="col-md-2 col-sm-4">
          <div className="card p-2 text-center shadow-sm">
            <div className="text-muted small">Gi·ªù tƒÉng ca</div>
            <div className="fw-bold">{salary.overtimeHours || 0}</div>
          </div>
        </div>

        <div className="col-md-2 col-sm-4">
          <div className="card p-2 text-center shadow-sm">
            <div className="text-muted small">Ti·ªÅn tƒÉng ca</div>
            <div className="fw-bold text-success">
              {fmtMoney(salary.overtimePay)} ƒë
            </div>
          </div>
        </div>
      </div>

      {/* Chi ti·∫øt */}
      <div className="card shadow-sm p-3">
        <h5 className="mb-3">üìã Chi ti·∫øt l∆∞∆°ng</h5>

        <div className="table-responsive">
          <table className="table table-bordered table-striped align-middle">
