import React, { useEffect, useState } from "react";
import api from "../../api";
import AdminLayout from "../../layouts/AdminLayout";
import { DEPARTMENTS, POSITIONS, ROLES } from "../../config/constants";

export default function HRManagement() {
  const [list, setList] = useState([]);
  const [editId, setEditId] = useState(null);

  const [form, setForm] = useState({
    username: "",
    password: "",
    name: "",
    department: "",
    position: "",
    role: "employee",
  });

  const load = async () => {
    try {
      const res = await api.get("/admin/employees");
      setList(res.data);
    } catch (err) {
      alert("‚ùå L·ªói t·∫£i danh s√°ch nh√¢n s·ª±");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // Submit form th√™m/s·ª≠a
  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await api.put(`/admin/employees/${editId}`, form);
        alert("‚úÖ C·∫≠p nh·∫≠t nh√¢n vi√™n th√†nh c√¥ng");
      } else {
        await api.post("/admin/employees", form);
        alert("‚úÖ Th√™m nh√¢n vi√™n th√†nh c√¥ng");
      }

      setForm({
        username: "",
        password: "",
        name: "",
        department: "",
        position: "",
        role: "employee",
      });
      setEditId(null);
      load();
    } catch (err) {
      alert(err.response?.data?.error || "‚ùå L·ªói");
    }
  };

  // Ch·ªânh s·ª≠a nh√¢n vi√™n
  const onEdit = (emp) => {
    setEditId(emp._id);
    setForm({
      username: emp.userId?.username,
      password: "",
      name: emp.name,
      department: emp.department,
      position: emp.position,
      role: emp.userId?.role,
    });
  };

  // X√≥a nh√¢n vi√™n
  const onDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a nh√¢n vi√™n n√†y?")) return;
    try {
      await api.delete(`/admin/employees/${id}`);
      alert("üóëÔ∏è ƒê√£ x√≥a nh√¢n vi√™n");
      load();
    } catch {
      alert("‚ùå Kh√¥ng th·ªÉ x√≥a");
    }
  };

  return (
    <AdminLayout>
      <h2 className="mb-4">üë®‚Äçüíº Qu·∫£n l√Ω nh√¢n s·ª±</h2>

      {/* ======================
           Form th√™m / s·ª≠a
      ====================== */}
      <div className="card p-3 mb-4">
        <h5>{editId ? "‚úèÔ∏è Ch·ªânh s·ª≠a nh√¢n vi√™n" : "‚ûï Th√™m nh√¢n vi√™n m·ªõi"}</h5>

        {/* Username + password */}
        <form onSubmit={onSubmit} className="mt-3">
          <div className="d-flex gap-3 flex-wrap">
            <div style={{ flex: "1 1 250px" }}>
              <label>Username</label>
              <input
                className="form-control"
                value={form.username}
                disabled={!!editId}
                onChange={(e) => setForm({ ...form, username: e.target.value })}
                required
              />
            </div>

            {!editId && (
              <div style={{ flex: "1 1 250px" }}>
                <label>Password</label>
                <input
                  type="password"
                  className="form-control"
                  value={form.password}
                  onChange={(e) => setForm({ ...form, password: e.target.value })}
                  required
                />
              </div>
            )}
          </div>

          {/* H·ªç t√™n + ph√≤ng ban + ch·ª©c v·ª• */}
          <div className="d-flex gap-3 flex-wrap mt-3">
            <div style={{ flex: "1 1 250px" }}>
              <label>H·ªç t√™n</label>
              <input
                className="form-control"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                required
              />
            </div>

            <div style={{ flex: "1 1 200px" }}>
              <label>Ph√≤ng ban</label>
              <select
                className="form-control"
                value={form.department}
                onChange={(e) =>
                  setForm({ ...form, department: e.target.value })
                }
                required
              >
                <option value="">-- Ch·ªçn ph√≤ng ban --</option>
                {DEPARTMENTS.map((d) => (
                  <option key={d} value={d}>
                    {d}
                  </option>
                ))}
              </select>
            </div>

            <div style={{ flex: "1 1 200px" }}>
              <label>Ch·ª©c v·ª•</label>
              <select
                className="form-control"
                value={form.position}
                onChange={(e) =>
                  setForm({ ...form, position: e.target.value })
                }
                required
              >
                <option value="">-- Ch·ªçn ch·ª©c v·ª• --</option>
                {POSITIONS.map((p) => (
                  <option key={p} value={p}>
                    {p}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Role + n√∫t */}
          <div className="d-flex gap-3 flex-wrap mt-3">
            <div style={{ flex: "1 1 200px" }}>
              <label>Quy·ªÅn t√†i kho·∫£n</label>
              <select
                className="form-control"
                value={form.role}
                onChange={(e) => setForm({ ...form, role: e.target.value })}
              >
                {ROLES.map((r) => (
                  <option key={r} value={r}>
                    {r}
                  </option>
                ))}
              </select>
            </div>

            <div
              style={{ flex: "1 1 150px" }}
              className="d-flex align-items-end"
            >
              <button className="btn btn-primary w-100">
                {editId ? "C·∫≠p nh·∫≠t" : "Th√™m nh√¢n vi√™n"}
              </button>
            </div>

