// frontend/src/pages/admin/PerformanceReviewAdmin.js
import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function PerformanceReviewAdmin() {
  const [list, setList] = useState([]);
  const [form, setForm] = useState({ userId: "", review: "" });

  const load = async () => {
    try {
      const res = await api.get("/admin/performance");
      setList(res.data);
    } catch (err) {
      console.error(err);
      alert("‚ùå L·ªói t·∫£i ƒë√°nh gi√°");
    }
  };

  useEffect(() => { load(); }, []);

  const add = async () => {
    try {
      await api.post("/admin/performance", form);
      setForm({ userId: "", review: "" });
      load();
      alert("‚úÖ Th√™m ƒë√°nh gi√° th√†nh c√¥ng");
    } catch (err) {
      console.error(err);
      alert("‚ùå L·ªói th√™m ƒë√°nh gi√°");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üìë ƒê√°nh gi√° hi·ªáu su·∫•t (Admin)</h3>
          <div className="mb-3">
            <input
              className="form-control mb-2"
              placeholder="UserId"
              value={form.userId}
              onChange={(e) => setForm({ ...form, userId: e.target.value })}
            />
            <textarea
              className="form-control mb-2"
              placeholder="N·ªôi dung ƒë√°nh gi√°"
              value={form.review}
              onChange={(e) => setForm({ ...form, review: e.target.value })}
            />
            <button className="btn btn-primary" onClick={add}>Th√™m ƒë√°nh gi√°</button>
          </div>

          <ul className="list-group">
            {list.map((r) => (
              <li key={r._id} className="list-group-item">
                <strong>{r.userId?.username}</strong>: {r.review}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default PerformanceReviewAdmin;
