// src/pages/admin/AttendanceManagement.js
import React, { useEffect, useState } from "react";
import SidebarMenu from "../../components/SidebarMenu";
import api from "../../api";

function AttendanceManagement() {
  const [list, setList] = useState([]);

  const load = async () => {
    try {
      const res = await api.get("/admin/attendance"); // API admin lấy tất cả chấm công
      setList(res.data);
    } catch (err) {
      console.error("❌ Lỗi tải chấm công:", err);
      alert("Không tải được dữ liệu chấm công");
    }
  };

  useEffect(() => {
    load();
  }, []);

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3">
          <SidebarMenu role="admin" />
        </div>
        <div className="col-9">
          <h3>⏱ Quản lý chấm công</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Nhân viên</th>
                <th>Ngày</th>
                <th>Giờ check-in</th>
                <th>Giờ check-out</th>
                <th>Tổng giờ</th>
              </tr>
            </thead>
            <tbody>
              {list.map((a) => (
                <tr key={a._id}>
                  <td>{a.userId?.username}</td>
                  <td>{new Date(a.date).toLocaleDateString()}</td>
                  <td>{a.checkIn ? new Date(a.checkIn).toLocaleTimeString() : "-"}</td>
                  <td>{a.checkOut ? new Date(a.checkOut).toLocaleTimeString() : "-"}</td>
                  <td>{a.totalHours || 0}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default AttendanceManagement;
