          <h3>üìÇ Qu·∫£n l√Ω t√†i li·ªáu</h3>
          <div className="card p-3 mb-3">
            <input className="form-control mb-2" placeholder="Ti√™u ƒë·ªÅ"
              value={form.title} onChange={(e) => setForm({ ...form, title: e.target.value })} />
            <input className="form-control mb-2" placeholder="ƒê∆∞·ªùng d·∫´n (URL)"
              value={form.url} onChange={(e) => setForm({ ...form, url: e.target.value })} />
            <button className="btn btn-primary" onClick={addDoc}>Th√™m</button>
          </div>

          <ul className="list-group">
            {docs.map(d => (
              <li key={d._id} className="list-group-item">
