import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Register() {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    name: "",
    department: "",
    position: "",
    role: "employee", // mặc định nhân viên
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/auth/register", formData);
      alert("Đăng ký thành công! Vui lòng đăng nhập.");
      navigate("/");
    } catch (err) {
      alert(err.response?.data?.error || "Có lỗi xảy ra");
    }
  };

  return (
    <div className="container mt-5">
      <h2>Đăng ký tài khoản</h2>
      <form onSubmit={handleSubmit}>
        <input className="form-control mb-2" name="username" placeholder="Username" onChange={handleChange} />
        <input className="form-control mb-2" name="password" type="password" placeholder="Password" onChange={handleChange} />
        <input className="form-control mb-2" name="name" placeholder="Họ tên" onChange={handleChange} />
        <input className="form-control mb-2" name="department" placeholder="Phòng ban" onChange={handleChange} />
        <input className="form-control mb-2" name="position" placeholder="Chức vụ" onChange={handleChange} />
        
        <select className="form-control mb-3" name="role" onChange={handleChange}>
          <option value="employee">Nhân viên</option>
          <option value="manager">Quản lý</option>
          <option value="admin">Admin</option>
        </select>

        <button className="btn btn-primary">Đăng ký</button>
      </form>
    </div>
  );
}

export default Register;
