// src/pages/user/Notifications.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import UserLayout from "../../layouts/UserLayout";
import { useAuth } from "../../context/AuthContext";

export default function Notifications() {
  const { user } = useAuth();   // üî• l·∫•y user t·ª´ context
  const [notis, setNotis] = useState([]);
  const [loading, setLoading] = useState(true);

  // N·∫øu user ch∆∞a t·∫£i ‚Üí tr√°nh redirect sai ‚Üí tr√°nh vƒÉng ƒëƒÉng nh·∫≠p
  if (!user) {
    return (
      <UserLayout>
        <div className="p-3 text-center">ƒêang t·∫£i d·ªØ li·ªáu...</div>
      </UserLayout>
    );
  }

  const load = async () => {
    try {
      const res = await api.get("/notifications");
      setNotis(res.data || []);
    } catch (err) {
      console.error("‚ùå L·ªói load th√¥ng b√°o:", err);
      alert("Kh√¥ng th·ªÉ t·∫£i th√¥ng b√°o.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, []);

  return (
    <UserLayout role={user.role}>   {/* üî• Fix vƒÉng login */}
      <h2 className="mb-3">üì¢ Th√¥ng b√°o</h2>

      <div className="card p-3">
        {loading ? (
          <p>‚è≥ ƒêang t·∫£i d·ªØ li·ªáu...</p>
        ) : notis.length === 0 ? (
          <p className="text-muted">Kh√¥ng c√≥ th√¥ng b√°o n√†o.</p>
        ) : (
          <div className="list-group">
            {notis.map((n) => (
              <div key={n._id} className="list-group-item list-group-item-action">
                <div className="d-flex justify-content-between">
                  <h5 className="mb-1">{n.title}</h5>
                  <small className="text-muted">
                    {new Date(n.createdAt).toLocaleString("vi-VN")}
                  </small>
                </div>

                <p className="mb-1">{n.message}</p>

                <small className="text-muted">
                  üë§ {n.createdBy?.username || "H·ªá th·ªëng"}
                </small>
              </div>
            ))}
          </div>
        )}
      </div>
    </UserLayout>
  );
}
