const express = require("express");
const router = express.Router();
const auth = require("../middleware/authMiddleware");
const Attendance = require("../models/Attendance");
const Employee = require("../models/Employee");

console.log("üî• attendanceRoutes LOADED:", __filename);

// =======================================================
// üìå 1) L·∫§Y L·ªäCH S·ª¨ CH·∫§M C√îNG + L·ªåC THEO NG√ÄY
// =======================================================
router.get("/", auth(["admin", "manager", "employee"]), async (req, res) => {
  try {
    const { date } = req.query; // ‚Üê nh·∫≠n ng√†y filter

    let attendanceQuery = {};
    if (date) attendanceQuery.date = date;

    let employees, attendanceRecords;

    if (req.user.role === "admin" || req.user.role === "manager") {
      employees = await Employee.find().populate("userId", "username role");
      attendanceRecords = await Attendance.find(attendanceQuery).populate(
        "userId",
        "username role"
      );
    } else {
      employees = await Employee.find({ userId: req.user.id }).populate(
        "userId",
        "username role"
      );

      attendanceRecords = await Attendance.find({
        ...attendanceQuery,
        userId: req.user.id,
      }).populate("userId", "username role");
    }

    // N·∫øu c√≥ filter ng√†y ‚Üí ch·ªâ tr·∫£ b·∫£n ghi (kh√¥ng t·∫°o d√≤ng tr·ªëng)
    if (date) {
      return res.json(
        attendanceRecords.sort((a, b) =>
          (a.userId?.username || "").localeCompare(b.userId?.username || "")
        )
      );
    }

    // Kh√¥ng l·ªçc ‚Üí map nh√¢n vi√™n + b·∫£n ghi
    const attendanceMap = new Map();
    attendanceRecords.forEach((a) => {
      const uid = a.userId?._id.toString();
      if (!attendanceMap.has(uid)) attendanceMap.set(uid, []);
      attendanceMap.get(uid).push(a);
    });

    const result = [];
    for (const emp of employees) {
      const uid = emp.userId?._id.toString();
      const records = attendanceMap.get(uid);

      if (records && records.length > 0) {
        result.push(...records);
      } else {
        result.push({
          _id: uid + "-empty",
          userId: emp.userId,
          date: null,
          checkIn: null,
          checkOut: null,
          lateMinutes: 0,
          overtimeHours: 0,
          totalDays: 0,
        });
      }
    }

    result.sort((a, b) =>
      (a.userId?.username || "").localeCompare(b.userId?.username || "")
    );

    res.json(result);
  } catch (err) {
    console.error("‚ùå L·ªói l·∫•y ch·∫•m c√¥ng:", err);
    res.status(500).json({ error: err.message });
  }
});

// =======================================================
// üìå 2) CH·∫§M C√îNG ƒê·ªíNG TH·ªúI
// =======================================================
router.post("/bulk-checkin", auth(["admin", "manager"]), async (req, res) => {
  try {
    const { userIds } = req.body;
    if (!Array.isArray(userIds) || userIds.length === 0)
      return res.status(400).json({ error: "Ch∆∞a ch·ªçn nh√¢n vi√™n" });

    const now = new Date();
    const today = now.toISOString().split("T")[0];
    const lateMinutes = Math.max(0, now.getHours() * 60 + now.getMinutes() - 7 * 60);

    const results = [];

    for (const uid of userIds) {
      await Attendance.findOneAndDelete({ userId: uid, date: today });

      const rec = await Attendance.create({
        userId: uid,
        date: today,
        checkIn: now,
        lateMinutes,
        totalDays: 1,
        overtimeHours: 0,
        status: "Present",
      });

      results.push(rec);
    }

    res.json({
      message: "‚úî ƒê√£ ch·∫•m c√¥ng ƒë·ªìng th·ªùi",
      count: results.length,
      records: results,
    });
  } catch (err) {
    console.error("‚ùå bulk-checkin ERROR:", err);
    res.status(500).json({ error: err.message });
  }
});


// =======================================================
// üìå 3) UPDATE
// =======================================================
router.put("/manual/:id", auth(["admin", "manager"]), async (req, res) => {
  try {
    const updated = await Attendance.findByIdAndUpdate(
      req.params.id,
      req.body,
      { new: true }
    );

    if (!updated) return res.status(404).json({ error: "Kh√¥ng t√¨m th·∫•y b·∫£n ghi" });

    res.json({ message: "‚úî ƒê√£ c·∫≠p nh·∫≠t", updated });
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
});

// =======================================================
// üìå 4) DELETE
// =======================================================
router.delete("/manual/:id", auth(["admin", "manager"]), async (req, res) => {
  try {
    await Attendance.findByIdAndDelete(req.params.id);
    res.json({ message: "üóë ƒê√£ x√≥a b·∫£n ghi" });
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
});

module.exports = router;
