import React, { useEffect, useState } from "react";
import api from "../../api";
import UserLayout from "../../layouts/UserLayout";

export default function DocumentManagementUser() {
  const [docs, setDocs] = useState([]);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [loading, setLoading] = useState(false);

  const [currentUserId, setCurrentUserId] = useState(null);
  const [currentRole, setCurrentRole] = useState(null);
  const [currentUserDept, setCurrentUserDept] = useState(null);

  // ===== L·∫•y userId + role t·ª´ token =====
  useEffect(() => {
    const token =
      localStorage.getItem("token") ||
      JSON.parse(localStorage.getItem("authUser") || "{}")?.token;

    if (token) {
      try {
        const decoded = JSON.parse(atob(token.split(".")[1]));
        setCurrentUserId(decoded?.id);
        setCurrentRole(decoded?.role);
      } catch (err) {
        console.error("‚ùå Token decode error:", err);
      }
    }
  }, []);

  // ===== L·∫•y ph√≤ng ban ng∆∞·ªùi d√πng =====
  useEffect(() => {
    const loadDept = async () => {
      try {
        const res = await api.get("/employees/me");
        setCurrentUserDept(res.data.department);
      } catch (err) {
        console.error("‚ùå Error load department:", err);
      }
    };
    loadDept();
  }, []);

  // ===== Load t√†i li·ªáu =====
  const load = async () => {
    try {
      const res = await api.get("/employees/documents");
      setDocs(res.data || []);
    } catch (err) {
      console.error("‚ùå Error load documents:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ t·∫£i t√†i li·ªáu!");
    }
  };

  useEffect(() => {
    load();
  }, []);

  // ===== Download =====
  const downloadFile = async (id) => {
    try {
      const response = await api.get(`/employees/documents/download/${id}`, {
        responseType: "blob",
      });

      const disposition = response.headers["content-disposition"];
      let filename = "tai-lieu";

      if (disposition) {
        const utf8 = disposition.match(/filename\*=UTF-8''(.+)/);
        const normal = disposition.match(/filename="(.+)"/);
        if (utf8) filename = decodeURIComponent(utf8[1]);
        else if (normal) filename = decodeURIComponent(normal[1]);
      }

      const blob = new Blob([response.data]);
      const link = document.createElement("a");
      link.href = window.URL.createObjectURL(blob);
      link.setAttribute("download", filename);
      link.click();
    } catch (err) {
      console.error("‚ùå Error download:", err);
      alert("Kh√¥ng th·ªÉ t·∫£i t√†i li·ªáu!");
    }
  };

  // ===== Upload =====
  const uploadFile = async (e) => {
    e.preventDefault();
    if (!file || !title.trim()) return alert("Vui l√≤ng nh·∫≠p ti√™u ƒë·ªÅ & ch·ªçn file!");

    const formData = new FormData();
    formData.append("title", title);
    formData.append("file", file);

    try {
      setLoading(true);
      await api.post("/employees/documents", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("üì§ T·∫£i l√™n th√†nh c√¥ng!");
      setTitle("");
      setFile(null);
      load();
    } catch (err) {
      console.error("‚ùå Error upload:", err);
      alert(err.response?.data?.error || "Kh√¥ng th·ªÉ t·∫£i l√™n!");
    } finally {
      setLoading(false);
    }
  };

  // ===== X√≥a =====
  const deleteFile = async (id) => {
    if (!window.confirm("B·∫°n ch·∫Øc ch·∫Øn mu·ªën x√≥a?")) return;
    try {
      await api.delete(`/employees/documents/${id}`);
      load();
    } catch (err) {
      console.error("‚ùå Error delete:", err);
      alert("Kh√¥ng th·ªÉ x√≥a!");
    }
  };

  // ===== L·ªçc t√†i li·ªáu =====
  const visibleDocs = docs.filter(
    (d) => d.department === "general" || d.department === currentUserDept
  );

  // ===== UI =====
  return (
    <UserLayout role="employee">
      <h2 className="mb-3">üìÇ T√†i li·ªáu c·ªßa t√¥i</h2>

      {/* Upload */}
      <div className="card p-3 mb-4">
        <h5 className="mb-3">‚¨ÜÔ∏è Upload t√†i li·ªáu</h5>

        <form onSubmit={uploadFile}>
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Ti√™u ƒë·ªÅ t√†i li·ªáu..."
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />

          <input
            type="file"
            className="form-control mb-2"
            onChange={(e) => setFile(e.target.files[0])}
            required
          />

          <button className="btn btn-primary" disabled={loading}>
            {loading ? "ƒêang t·∫£i..." : "üì§ T·∫£i l√™n"}
          </button>
        </form>
      </div>

      {/* Document list */}
      <div className="card p-3">
        <h5>üìë Danh s√°ch t√†i li·ªáu</h5>

        {visibleDocs.length === 0 ? (
          <p className="mt-2">Kh√¥ng c√≥ t√†i li·ªáu n√†o.</p>
        ) : (
          <table className="table table-bordered table-striped mt-3">
            <thead>
              <tr>
                <th>Ph√≤ng ban</th>
                <th>Th∆∞ m·ª•c</th>
                <th>Ti√™u ƒë·ªÅ</th>
                <th>Ng∆∞·ªùi t·∫£i l√™n</th>
                <th className="text-center">H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {visibleDocs.map((d) => {
                const uploadedId = d.uploadedBy?._id || d.uploadedBy;
                const isOwner = String(uploadedId) === String(currentUserId);
                const isAdmin = currentRole === "admin";
                const sameDept =
                  d.department !== "general" && d.department === currentUserDept;

                return (
                  <tr key={d._id}>
                    <td>{d.department}</td>
                    <td>{d.folder || "‚Äî"}</td>
                    <td>{d.title}</td>
                    <td>{d.uploadedBy?.username || "Kh√¥ng r√µ"}</td>
                    <td className="text-center">
                      <button
                        className="btn btn-sm btn-success me-2"
                        onClick={() => downloadFile(d._id)}
                      >
                        ‚¨áÔ∏è T·∫£i
                      </button>

                      {(isOwner || isAdmin || sameDept) && (
                        <button
                          className="btn btn-sm btn-danger"
                          onClick={() => deleteFile(d._id)}
                        >
                          üóëÔ∏è X√≥a
                        </button>
                      )}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        )}
      </div>
    </UserLayout>
  );
}
