import React, { useEffect, useMemo, useRef, useState } from "react";
import io from "socket.io-client";
import api from "../../api";
import UserLayout from "../../layouts/UserLayout";
import { useAuth } from "../../context/AuthContext";

const SOCKET_BASE =
  (process.env.REACT_APP_API_BASE || "http://localhost:5000").replace("/api", "");

const socket = io(SOCKET_BASE, { transports: ["websocket"], autoConnect: true });

export default function Chat() {
  const { user } = useAuth();

  // L·∫•y user th·∫≠t t·ª´ context
  const me = user || {};

  const [deptRoom, setDeptRoom] = useState(null);
  const [privateRooms, setPrivateRooms] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [chatOutsideDept, setChatOutsideDept] = useState(false);

  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");

  const bottomRef = useRef(null);
  const joinedRoomIdRef = useRef(null);

  // L·∫•y ID t·ª´ user
  const myId = useMemo(() => {
    return String(me._id || me.id || me.userId || "");
  }, [me]);

  const extractId = (val) => {
    if (!val) return "";
    if (typeof val === "string") return val;
    if (typeof val === "object") return String(val._id || val.id || "");
    return "";
  };

  const isMine = (m) => {
    const senderId = extractId(m.sender);
    return String(senderId) === String(myId);
  };

  const normalizeMsg = (raw) => ({
    _id: raw?._id || Math.random().toString(36).slice(2),
    roomId: raw?.roomId,
    sender: {
      _id: extractId(raw.sender),
      username: raw?.sender?.username || raw?.fromUserName || "N/A",
    },
    content: raw?.content || raw?.message || "",
    createdAt: raw?.createdAt || new Date().toISOString(),
  });

  // Join socket room
  useEffect(() => {
    if (myId)
      socket.emit("join", {
        userId: myId,
        department: me.department,
      });
  }, [myId, me.department]);

  // Load danh s√°ch ph√≤ng
  const loadRooms = async () => {
    try {
      const res = await api.get("/messages/rooms");
      setPrivateRooms(res.data.privateRooms || []);
      setDeptRoom(res.data.deptRoom || null);
    } catch (err) {
      console.error("‚ùå Rooms error:", err);
    }
  };

  // Load nh√¢n vi√™n
  const loadEmployees = async () => {
    try {
      const scope = chatOutsideDept ? "all" : "dept";
      const res = await api.get(`/employees/peers?scope=${scope}`);
      setEmployees(res.data || []);
    } catch {
      setEmployees([]);
    }
  };

  useEffect(() => { loadRooms(); }, []);
  useEffect(() => { loadEmployees(); }, [chatOutsideDept]);

  const openRoom = async (room) => {
    if (!room?._id) return;

    if (joinedRoomIdRef.current)
      socket.emit("leave_room", { roomId: joinedRoomIdRef.current });

    socket.emit("join_room", { roomId: room._id });
    joinedRoomIdRef.current = room._id;

    setCurrentRoom(room);
    loadMessages(room);
  };

  const loadMessages = async (room) => {
    try {
      const res = await api.get(`/messages/${room._id}`);
      setMessages((res.data || []).map(normalizeMsg));

      setTimeout(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), 60);
    } catch {
      setMessages([]);
    }
  };

  // Receive realtime
  useEffect(() => {
    const handler = (payload) => {
      if (!currentRoom) return;
      if (String(payload.roomId) !== String(currentRoom._id)) return;
      if (String(payload.fromUserId) === String(myId)) return; // ignore my own event

      setMessages((prev) => [...prev, normalizeMsg(payload)]);
      setTimeout(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), 60);
    };

    socket.on("receive_message", handler);
    return () => socket.off("receive_message", handler);
  }, [currentRoom, myId]);

  // Send message
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!currentRoom || !text.trim()) return;

    const content = text.trim();

    try {
      const res = await api.post("/messages", {
        roomId: currentRoom._id,
        content,
      });

      const localMsg = normalizeMsg(res.data);
      localMsg.sender = { _id: myId, username: me.username || "T√¥i" };

      socket.emit("send_message", {
        roomId: currentRoom._id,
        content,
        fromUserId: myId,
        fromUserName: me.username,
        createdAt: new Date().toISOString(),
      });

      setMessages((prev) => [...prev, localMsg]);
      setText("");

      setTimeout(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), 60);
    } catch (err) {
      console.error("‚ùå Send error:", err);
    }
  };

  const startPrivateWith = async (otherId) => {
    try {
      const res = await api.post("/messages/rooms/private", { otherUserId: otherId });
      await loadRooms();
      openRoom(res.data);
    } catch {}
  };

  const renderBubble = (m) => {
    const mine = isMine(m);

    return (
      <div
        key={m._id}
        className={`d-flex mb-2 ${mine ? "justify-content-end" : "justify-content-start"}`}
      >
        <div
          className={`p-2 px-3 rounded-4 shadow-sm ${
            mine ? "bg-primary text-white" : "bg-light"
          }`}
          style={{
            maxWidth: "70%",
            borderRadius: mine ? "18px 18px 2px 18px" : "18px 18px 18px 2px",
          }}
        >
          {!mine && <div className="small text-muted mb-1">{m.sender?.username}</div>}
          <div>{m.content}</div>

          <div className="small text-muted mt-1" style={{ fontSize: "0.75rem" }}>
            {new Date(m.createdAt).toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}
          </div>
        </div>
      </div>
    );
  };

  const privateTitle = (room) => {
    const others = room.participants.filter((p) => extractId(p) !== myId);
    return others.map((x) => x.username).join(", ");
  };

  return (
    <UserLayout>
      <h2 className="mb-3">üí¨ Chat</h2>

      <div className="d-flex gap-3">
        {/* LEFT PANEL */}
        <div style={{ width: 300 }}>
          <div className="card p-2">
            <h5>Ph√≤ng chat</h5>

            <div className="form-check form-switch my-2">
              <input
                type="checkbox"
                checked={chatOutsideDept}
                onChange={() => setChatOutsideDept((v) => !v)}
                className="form-check-input"
              />
              <label className="form-check-label">Chat ngo√†i ph√≤ng ban</label>
            </div>

            {deptRoom && (
              <div className="mb-2">
                <div className="d-flex justify-content-between">
                  <strong>üë• {deptRoom.name}</strong>
                  <button className="btn btn-sm btn-outline-primary" onClick={() => openRoom(deptRoom)}>
                    M·ªü
                  </button>
                </div>
              </div>
            )}

