// src/pages/admin/DocumentManagementAdmin.js
import React, { useEffect, useState } from "react";
import api from "../../api";
import SidebarMenu from "../../components/SidebarMenu";

function DocumentManagementAdmin() {
  const [docs, setDocs] = useState([]);
  const [folders, setFolders] = useState([]); // üìÅ Danh s√°ch th∆∞ m·ª•c
  const [newFolder, setNewFolder] = useState("");
  const [form, setForm] = useState({ folder: "", department: "general", files: [] });

  // T·∫£i danh s√°ch t√†i li·ªáu
  const load = async () => {
    const res = await api.get("/admin/documents");
    setDocs(res.data);

    // L·∫•y danh s√°ch th∆∞ m·ª•c duy nh·∫•t
    const uniqueFolders = [...new Set(res.data.map(d => d.folder || "Chung"))];
    setFolders(uniqueFolders);
  };

  useEffect(() => { load(); }, []);

  // T·∫°o th∆∞ m·ª•c m·ªõi
  const createFolder = async () => {
    if (!newFolder.trim()) return alert("Nh·∫≠p t√™n th∆∞ m·ª•c!");
    try {
      await api.post("/admin/documents/folder", { folder: newFolder });
      alert("‚úÖ ƒê√£ t·∫°o th∆∞ m·ª•c m·ªõi");
      setNewFolder("");
      load();
    } catch (err) {
      alert(err.response?.data?.error || "L·ªói khi t·∫°o th∆∞ m·ª•c");
    }
  };

  // Upload file v√†o th∆∞ m·ª•c
  const handleUpload = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      fd.append("folder", form.folder);
      fd.append("department", form.department);
      for (const f of form.files) fd.append("files", f);

      await api.post("/admin/documents", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("‚úÖ Upload th√†nh c√¥ng!");
      setForm({ folder: "", department: "general", files: [] });
      load();
    } catch (err) {
      console.error(err);
      alert("‚ùå L·ªói upload");
    }
  };

  // T·∫£i file
  const handleDownload = async (id, filename) => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch(`http://localhost:5000/api/admin/documents/download/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const blob = await res.blob();
      const a = document.createElement("a");
      a.href = window.URL.createObjectURL(blob);
      a.download = filename;
      a.click();
    } catch (err) {
      alert("Kh√¥ng th·ªÉ t·∫£i file");
    }
  };

  // X√≥a file
  const handleDelete = async (id) => {
    if (!window.confirm("X√≥a t√†i li·ªáu n√†y?")) return;
    await api.delete(`/admin/documents/${id}`);
    load();
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-3"><SidebarMenu role="admin" /></div>
        <div className="col-9">
          <h3>üìÇ Qu·∫£n l√Ω t√†i li·ªáu</h3>

          {/* === T·∫°o th∆∞ m·ª•c === */}
          <div className="mb-4">
            <h5>üóÇ T·∫°o th∆∞ m·ª•c m·ªõi</h5>
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                placeholder="T√™n th∆∞ m·ª•c"
                value={newFolder}
                onChange={(e) => setNewFolder(e.target.value)}
              />
              <button onClick={createFolder} className="btn btn-primary">‚ûï T·∫°o</button>
            </div>
          </div>

          {/* === Upload file === */}
          <form onSubmit={handleUpload} className="mb-4">
            <h5>üì§ Upload t√†i li·ªáu</h5>
            <select
              className="form-control mb-2"
              value={form.folder}
              onChange={(e) => setForm({ ...form, folder: e.target.value })}
              required
            >
              <option value="">-- Ch·ªçn th∆∞ m·ª•c --</option>
              {folders.map((f) => (
                <option key={f} value={f}>{f}</option>
              ))}
            </select>

            <select
              className="form-control mb-2"
              value={form.department}
              onChange={(e) => setForm({ ...form, department: e.target.value })}
            >
              <option value="general">Chung</option>
              <option value="IT">IT</option>
              <option value="HR">HR</option>
              <option value="Finance">Finance</option>
            </select>

            <input
              type="file"
              multiple
              className="form-control mb-2"
              onChange={(e) => setForm({ ...form, files: Array.from(e.target.files) })}
              required
            />
            <button className="btn btn-success">‚¨ÜÔ∏è Upload</button>
          </form>

          {/* === Danh s√°ch t√†i li·ªáu === */}
          {folders.map((folder) => (
            <div key={folder} className="mb-4">
              <h5 className="bg-light p-2 rounded">üìÅ {folder}</h5>
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>Ti√™u ƒë·ªÅ</th>
                    <th>Ph√≤ng ban</th>
                    <th>Ng∆∞·ªùi t·∫£i l√™n</th>
                    <th>H√†nh ƒë·ªông</th>
                  </tr>
                </thead>
                <tbody>
                  {docs
                    .filter((d) => d.folder === folder)
                    .map((d) => (
                      <tr key={d._id}>
                        <td>{d.title}</td>
                        <td>{d.department}</td>
                        <td>{d.uploadedBy?.username}</td>
                        <td>
                          <button
                            onClick={() => handleDownload(d._id, d.title)}
                            className="btn btn-sm btn-success me-2"
                          >
                            ‚¨áÔ∏è T·∫£i
                          </button>
                          <button
                            onClick={() => handleDelete(d._id)}
                            className="btn btn-sm btn-danger"
                          >
                            üóë X√≥a
                          </button>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default DocumentManagementAdmin;
